import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from "../../context/AuthContext";
import { rolePermissions } from "../../context/permissions";

const UserManagement = () => {
  const { user } = useContext(AuthContext);
  const permissions = rolePermissions[user.role] || {};
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    employeeId: '',
    role: 'employee',
    password: '',
    confirmPassword: '',
    division: '',
    section: ''
  });
  const [divisions, setDivisions] = useState([]);
  const [sections, setSections] = useState([]);

  // Helper function to get division name by ID
  const getDivisionName = (divisionId) => {
    const division = divisions.find(div => (div._id || div.id) === divisionId);
    return division ? division.name : 'N/A';
  };

  // Helper function to get section name by ID
  const getSectionName = (sectionId) => {
    const section = sections.find(sec => (sec._id || sec.id) === sectionId);
    return section ? section.name : 'N/A';
  };


  // Move handleEditUser above its first usage
  const handleEditUser = (user) => {
    setFormData({
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      employeeId: user.employeeId,
      role: user.role,
      password: '',
      confirmPassword: '',
      division: user.division || '',
      section: user.section || ''
    });
    setEditingUser(user);
    setShowAddModal(true);
  };

  useEffect(() => {
    // Fetch users and divisions from API
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        
        // Fetch users from database
        try {
          const usersResponse = await fetch('http://localhost:5000/api/users', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });
          
          if (usersResponse.ok) {
            const usersData = await usersResponse.json();
            // Map the data to match frontend structure
            const mappedUsers = usersData.data.map(user => ({
              id: user._id,
              firstName: user.firstName,
              lastName: user.lastName,
              email: user.email,
              role: user.role,
              employeeId: user.employeeId,
              status: user.isActive ? 'active' : 'inactive',
              division: user.division?._id || user.division,
              section: user.section?._id || user.section,
              divisionName: user.division?.name,
              sectionName: user.section?.name
            }));
            setUsers(mappedUsers);
          } else {
            console.error('Failed to fetch users');
            setUsers([]);
          }
        } catch (userError) {
          console.error('Error fetching users:', userError);
          setUsers([]);
        }
        
        // Fetch divisions from database
        try {
          const divisionsResponse = await fetch('http://localhost:5000/api/divisions', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });
          
          if (divisionsResponse.ok) {
            const divisionsData = await divisionsResponse.json();
            setDivisions(divisionsData.data || []);
          } else {
            console.error('Failed to fetch divisions');
            setDivisions([]);
          }
        } catch (divisionError) {
          console.error('Error fetching divisions:', divisionError);
          setDivisions([]);
        }

        // Fetch sections from database
        try {
          const sectionsResponse = await fetch('http://localhost:5000/api/sections', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });
          
          if (sectionsResponse.ok) {
            const sectionsData = await sectionsResponse.json();
            setSections(sectionsData.data || []);
          } else {
            console.error('Failed to fetch sections');
            setSections([]);
          }
        } catch (sectionError) {
          console.error('Error fetching sections:', sectionError);
          setSections([]);
        }
        
        setLoading(false);
        
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAddUser = () => {
    setFormData({
      firstName: '',
      lastName: '',
      email: '',
      employeeId: '',
      role: 'employee',
      password: '',
      confirmPassword: '',
      division: '',
      section: ''
    });
    setEditingUser(null);
    setShowAddModal(true);
  };

  // ...existing code...

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5000/api/users/${userId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (response.ok) {
          setUsers(users.filter(user => user.id !== userId));
          alert('User deleted successfully!');
        } else {
          const error = await response.json();
          alert(error.message || 'Failed to delete user');
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Error deleting user. Please try again.');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Check if passwords match when adding a new user
    if (!editingUser && formData.password !== formData.confirmPassword) {
      alert('Passwords do not match!');
      return;
    }
    
    // Validate required fields
    if (!formData.firstName || !formData.lastName || !formData.email || !formData.employeeId || !formData.role) {
      alert('Please fill in all required fields!');
      return;
    }
    
    if (!editingUser && !formData.password) {
      alert('Password is required for new users!');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const userData = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        employeeId: formData.employeeId,
        role: formData.role,
        division: formData.division || undefined,
        section: formData.section || undefined
      };

      if (!editingUser) {
        userData.password = formData.password;
      } else if (formData.password) {
        userData.password = formData.password;
      }

      let response;
      if (editingUser) {
        // Update existing user
        response = await fetch(`http://localhost:5000/api/users/${editingUser.id}`, {
          method: 'PUT',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(userData)
        });
      } else {
        // Create new user
        response = await fetch('http://localhost:5000/api/users', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(userData)
        });
      }

      if (response.ok) {
        const result = await response.json();
        
        if (editingUser) {
          // Update existing user in state
          setUsers(users.map(user => 
            user.id === editingUser.id 
              ? {
                  ...user,
                  firstName: formData.firstName,
                  lastName: formData.lastName,
                  email: formData.email,
                  employeeId: formData.employeeId,
                  role: formData.role,
                  division: formData.division,
                  section: formData.section
                }
              : user
          ));
        } else {
          // Add new user to state
          const newUser = {
            id: result.data._id,
            firstName: formData.firstName,
            lastName: formData.lastName,
            email: formData.email,
            employeeId: formData.employeeId,
            role: formData.role,
            status: 'active',
            division: formData.division,
            section: formData.section
          };
          setUsers([...users, newUser]);
        }
        
        setShowAddModal(false);
        setEditingUser(null);
        alert(editingUser ? 'User updated successfully!' : 'User created successfully!');
      } else {
        const error = await response.json();
        alert(error.message || 'Failed to save user');
      }
    } catch (error) {
      console.error('Error saving user:', error);
      alert('Error saving user. Please try again.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="user-management">
      {/* Professional Section Header */}
      <div className="section-header" style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
        <h2 style={{ marginRight: 'auto' }}><i className="bi bi-people"></i> User Management</h2>
        <button 
          className="btn-professional btn-secondary"
          style={{ marginRight: '8px' }}
          // TODO: Add onClick handler for Add Role modal or navigation
        >
          <i className="bi bi-person-plus"></i> Add Role
        </button>
        <button 
          className="btn-professional btn-primary"
          onClick={handleAddUser}
        >
          <i className="bi bi-plus-circle"></i> Add User
        </button>
      </div>

      {/* Professional Users Table */}
      <div className="professional-card">
        <div className="table-responsive">
          <table className="professional-table">
            <thead>
              <tr>
                <th>Employee ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
                <th>Division</th>
                <th>Section</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td><strong>{user.employeeId}</strong></td>
                  <td>{`${user.firstName} ${user.lastName}`}</td>
                  <td>{user.email}</td>
                  <td>
                    <span className={`role-badge role-${user.role.replace('_', '-')}`}>
                      {user.role.replace('_', ' ').toUpperCase()}
                    </span>
                  </td>
                  <td>{getDivisionName(user.division)}</td>
                  <td>{getSectionName(user.section)}</td>
                  <td>
                    <span className={`status-badge status-${user.status}`}>
                      {user.status.toUpperCase()}
                    </span>
                  </td>
                  <td>
                    <div style={{ display: 'flex', gap: '8px' }}>
                      {permissions.canEditUser ? (
                        <button 
                          className="btn-professional btn-primary"
                          onClick={() => handleEditUser(user)}
                          title="Edit User"
                          style={{ padding: '8px 12px', fontSize: '12px' }}
                        >
                          <i className="bi bi-pencil"></i>
                        </button>
                      ) : (
                        <button 
                          className="btn-professional btn-primary"
                          disabled
                          title="No permission to edit"
                          style={{ padding: '8px 12px', fontSize: '12px', opacity: 0.5, cursor: 'not-allowed' }}
                          onMouseOver={e => e.currentTarget.style.cursor = 'not-allowed'}
                          onMouseOut={e => e.currentTarget.style.cursor = 'default'}
                        >
                          <i className="bi bi-pencil"></i>
                        </button>
                      )}
                      {permissions.canDeleteUser ? (
                        <button 
                          className="btn-professional btn-danger"
                          onClick={() => handleDeleteUser(user.id)}
                          title="Delete User"
                          style={{ padding: '8px 12px', fontSize: '12px' }}
                        >
                          <i className="bi bi-trash"></i>
                        </button>
                      ) : (
                        <button 
                          className="btn-professional btn-danger"
                          disabled
                          title="No permission to delete"
                          style={{ padding: '8px 12px', fontSize: '12px', opacity: 0.5, cursor: 'not-allowed' }}
                          onMouseOver={e => e.currentTarget.style.cursor = 'not-allowed'}
                          onMouseOut={e => e.currentTarget.style.cursor = 'default'}
                        >
                          <i className="bi bi-trash"></i>
                        </button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {users.length === 0 && (
          <div className="no-data">
            <p>No users found. Click "Add User" to create the first user.</p>
          </div>
        )}
      </div>

      {/* Professional Add/Edit User Modal */}
      {showAddModal && (
        <div className="modal-overlay" onClick={() => setShowAddModal(false)}>
          <div className="modal-content professional-form" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header" style={{ borderBottom: '2px solid var(--gray-200)', paddingBottom: '20px', marginBottom: '24px' }}>
              <h3 style={{ fontSize: '24px', fontWeight: '700', color: 'var(--gray-900)', display: 'flex', alignItems: 'center', gap: '12px' }}>
                <i className="bi bi-person-plus" style={{ color: 'var(--primary)' }}></i>
                {editingUser ? 'Edit User' : 'Add New User'}
              </h3>
              <button 
                className="modal-close btn-professional btn-danger"
                onClick={() => setShowAddModal(false)}
                style={{ padding: '8px 12px', fontSize: '16px' }}
              >
                <i className="bi bi-x"></i>
              </button>
            </div>
            
            <form onSubmit={handleSubmit} className="modal-body">
              <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
                <div className="form-group">
                  <label className="form-label">First Name *</label>
                  <input
                    type="text"
                    name="firstName"
                    className="form-input"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="form-label">Last Name *</label>
                  <input
                    type="text"
                    name="lastName"
                    className="form-input"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>

              <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
                <div className="form-group">
                  <label className="form-label">Email *</label>
                  <input
                    type="email"
                    name="email"
                    className="form-input"
                    value={formData.email}
                    onChange={handleInputChange}
                    placeholder="user@slpa.lk"
                    required
                  />
                </div>
                <div className="form-group">
                  <label className="form-label">Employee ID *</label>
                  <input
                    type="text"
                    name="employeeId"
                    className="form-input"
                    value={formData.employeeId}
                    onChange={handleInputChange}
                    placeholder="e.g., EMP001"
                    required
                  />
                </div>
              </div>

              <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
                <div className="form-group">
                  <label className="form-label">Division *</label>
                  <select
                    name="division"
                    className="form-select"
                    value={formData.division}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select Division</option>
                    {divisions.map(division => (
                      <option key={division._id || division.id} value={division._id || division.id}>
                        {division.name} ({division.code})
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label className="form-label">Role *</label>
                  <select
                    name="role"
                    className="form-select"
                    value={formData.role}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="employee">Employee</option>
                    <option value="administrative_clerk">Administrative Clerk</option>
                    <option value="clerk">Clerk</option>
                    <option value="admin">Administrator</option>
                    <option value="super_admin">Super Admin</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Section</label>
                <select
                  name="section"
                  className="form-select"
                  value={formData.section}
                  onChange={handleInputChange}
                >
                  <option value="">Select Section</option>
                  {sections.map(section => (
                    <option key={section._id || section.id} value={section._id || section.id}>
                      {section.name} ({section.code})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
                <div className="form-group">
                  <label className="form-label">Password {!editingUser && '*'}</label>
                  <input
                    type="password"
                    name="password"
                    className="form-input"
                    value={formData.password}
                    onChange={handleInputChange}
                    placeholder={editingUser ? "Leave blank to keep current password" : "Enter password"}
                    required={!editingUser}
                    minLength="1"
                  />
                </div>
                <div className="form-group">
                  <label className="form-label">Confirm Password {!editingUser && '*'}</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    className="form-input"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    placeholder={editingUser ? "Leave blank to keep current password" : "Confirm password"}
                    required={!editingUser}
                    minLength="1"
                  />
                </div>
              </div>

              <div className="modal-footer" style={{ borderTop: '2px solid var(--gray-200)', paddingTop: '20px', marginTop: '24px', display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn-professional btn-secondary"
                  onClick={() => setShowAddModal(false)}
                  style={{ background: 'var(--gray-500)' }}
                >
                  Cancel
                </button>
                <button 
                  type="submit"
                  className="btn-professional btn-success"
                >
                  <i className="bi bi-check-circle"></i>
                  {editingUser ? 'Update User' : 'Add User'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagement;
