import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import logo from '../../assets/logo.jpg';

const Login = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      await login(userId, password);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen w-full flex items-center justify-center bg-cover bg-center relative"
      style={{ backgroundImage: "url('/background.jpg')" }}>
      <div className="absolute inset-0 bg-blue-900 bg-opacity-60 backdrop-blur-sm"></div>
      <div className="relative z-10 w-full max-w-3xl flex flex-col md:flex-row items-center bg-white bg-opacity-20 rounded-3xl shadow-2xl p-8 md:p-16">
        <div className="flex flex-col items-center w-full md:w-1/2 mb-8 md:mb-0">
          <img src={logo} alt="Logo" className="w-36 h-36 mb-4 rounded-full shadow-lg" />
          <h1 className="text-4xl font-bold tracking-widest text-gray-800 mb-2">LOGIN</h1>
          <div className="h-1 w-16 bg-gray-400 rounded-full mb-2"></div>
        </div>
        <form onSubmit={handleSubmit} className="w-full md:w-1/2 flex flex-col gap-4">
          {error && <div className="text-red-600 text-center">{error}</div>}
          <div>
            <label className="block text-gray-100 font-semibold mb-1" htmlFor="userId">
              USER ID
            </label>
            <input
              id="userId"
              type="text"
              className="pl-4 pr-4 py-2 w-full rounded-lg bg-white bg-opacity-80 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="SA1001"
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              required
              autoFocus
            />
          </div>
          <div>
            <label className="block text-gray-100 font-semibold mb-1" htmlFor="password">
              PASSWORD
            </label>
            <input
              id="password"
              type="password"
              className="pl-4 pr-4 py-2 w-full rounded-lg bg-white bg-opacity-80 focus:outline-none focus:ring-2 focus:ring-blue-400"
              placeholder="********"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="flex justify-end">
            <button
              type="button"
              className="text-blue-200 hover:underline text-sm"
              onClick={() => navigate('/forgot-password')}
            >
              Forgot Password?
            </button>
          </div>
          <button
            type="submit"
            className="w-full py-3 rounded-lg bg-gradient-to-r from-blue-500 to-blue-700 text-white font-bold text-lg tracking-wider shadow-lg hover:from-blue-600 hover:to-blue-800 transition"
            disabled={loading}
          >
            {loading ? 'Logging in...' : 'LOGIN'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
