import React, { useState, useEffect } from 'react';

const DivisionManagement = () => {
  const [divisions, setDivisions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);

  // Fetch divisions from API
  const fetchDivisions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/divisions', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setDivisions(data.data || []);
      } else {
        console.error('Failed to fetch divisions');
        // Fallback to mock data if API fails
        setDivisions([
          { _id: '1', name: 'Engineering', code: 'ENG', description: 'Technical Department', employeeCount: 120 },
          { _id: '2', name: 'HR', code: 'HR', description: 'Human Resources', employeeCount: 25 },
          { _id: '3', name: 'Finance', code: 'FIN', description: 'Financial Department', employeeCount: 35 }
        ]);
      }
    } catch (error) {
      console.error('Error fetching divisions:', error);
      // Fallback to mock data
      setDivisions([
        { _id: '1', name: 'Engineering', code: 'ENG', description: 'Technical Department', employeeCount: 120 },
        { _id: '2', name: 'HR', code: 'HR', description: 'Human Resources', employeeCount: 25 },
        { _id: '3', name: 'Finance', code: 'FIN', description: 'Financial Department', employeeCount: 35 }
      ]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading-container">Loading...</div>;
  }

  return (
    <div className="division-management">
      <div className="page-header">
        <h2><i className="bi bi-building"></i> Division Management</h2>
        <button className="btn btn-primary">
          <i className="bi bi-plus-circle"></i> Add Division
        </button>
      </div>

      <div className="divisions-grid">
        {divisions.map(division => (
          <div key={division.id} className="division-card">
            <h4>{division.name}</h4>
            <p>{division.description}</p>
            <div className="employee-count">
              <strong>{division.employeeCount}</strong> employees
            </div>
            <div className="actions">
              <button className="btn btn-sm btn-outline-primary">Edit</button>
              <button className="btn btn-sm btn-outline-danger">Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DivisionManagement;
