import React, { useContext } from 'react';
import { Navigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { usePermissions } from '../context/PermissionContext';

const ProtectedRoute = ({ children, requiredPermissions = [] }) => {
  const { user, loading: authLoading, isAuthenticated } = useContext(AuthContext);
  const { canAccess, loading: permLoading } = usePermissions();

  // Show loading screen while checking authentication and permissions
  if (authLoading || permLoading) {
    return (
      <div className="loading-screen d-flex justify-content-center align-items-center min-vh-100">
        <div className="text-center">
          <div className="spinner-border text-primary mb-3" role="status" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="text-muted">Verifying your session...</p>
        </div>
      </div>
    );
  }

  // Check if user is authenticated and account is active
  if (!isAuthenticated || !user) {
    return <Navigate to="/login" replace />;
  }

  // Check if user account is active
  if (user.isActive === false) {
    return (
      <div className="error-screen d-flex justify-content-center align-items-center min-vh-100">
        <div className="text-center">
          <h3 className="text-danger mb-3">Account Deactivated</h3>
          <p className="text-muted">Your account has been deactivated. Please contact the administrator.</p>
          <button 
            className="btn btn-primary"
            onClick={() => window.location.href = '/login'}
          >
            Back to Login
          </button>
        </div>
      </div>
    );
  }

  // Check if user has required permissions
  if (requiredPermissions.length > 0 && !canAccess(requiredPermissions)) {
    return (
      <div className="error-screen d-flex justify-content-center align-items-center min-vh-100">
        <div className="text-center">
          <div className="mb-4">
            <i className="bi bi-shield-exclamation" style={{ fontSize: '4rem', color: '#dc3545' }}></i>
          </div>
          <h3 className="text-danger mb-3">Access Denied</h3>
          <p className="text-muted mb-4">
            You don't have the required permissions to access this page.
          </p>
          <p className="small text-muted mb-4">
            Your role: <strong>{user.role}</strong><br/>
            Required permissions: <strong>{requiredPermissions.join(', ')}</strong>
          </p>
          <button 
            className="btn btn-primary me-2"
            onClick={() => window.history.back()}
          >
            <i className="bi bi-arrow-left me-2"></i>
            Go Back
          </button>
          <button 
            className="btn btn-outline-secondary"
            onClick={() => window.location.href = '/dashboard'}
          >
            <i className="bi bi-house me-2"></i>
            Dashboard
          </button>
        </div>
      </div>
    );
  }

  return children;
};

export default ProtectedRoute;
