import React, { useState, useEffect } from 'react';

const RoleAccessManagement = () => {
  const [roles, setRoles] = useState([]);
  const [permissions, setPermissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [currentRole, setCurrentRole] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    displayName: '',
    description: '',
    permissions: []
  });
  const [formErrors, setFormErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);

  // Available permissions list
  const availablePermissions = [
    { id: 'all_access', name: 'All Access', description: 'Full system access' },
    { id: 'user_management', name: 'User Management', description: 'Create, edit, delete users' },
    { id: 'role_management', name: 'Role Management', description: 'Manage roles and permissions' },
    { id: 'division_management', name: 'Division Management', description: 'Manage divisions' },
    { id: 'section_management', name: 'Section Management', description: 'Manage sections' },
    { id: 'attendance_management', name: 'Attendance Management', description: 'Manage attendance records' },
    { id: 'attendance_view', name: 'View Attendance', description: 'View attendance records' },
    { id: 'reports_view', name: 'View Reports', description: 'View system reports' },
    { id: 'reports_generate', name: 'Generate Reports', description: 'Generate and export reports' },
    { id: 'system_settings', name: 'System Settings', description: 'Configure system settings' },
    { id: 'audit_logs', name: 'Audit Logs', description: 'View audit logs' },
    { id: 'profile_edit', name: 'Edit Profile', description: 'Edit own profile' }
  ];

  // Fetch roles data
  const fetchData = async () => {
    try {
      // Mock roles data for now - replace with actual API call
      setRoles([
        {
          _id: '1',
          name: 'super_admin',
          displayName: 'Super Administrator',
          description: 'Full system access with all permissions',
          permissions: ['all_access', 'user_management', 'role_management', 'system_settings', 'audit_logs'],
          isSystem: true,
          isActive: true,
          createdAt: '2024-01-01'
        },
        {
          _id: '2',
          name: 'administrator',
          displayName: 'Administrator',
          description: 'Administrative access with user and content management',
          permissions: ['user_management', 'division_management', 'section_management', 'attendance_management', 'reports_generate'],
          isSystem: true,
          isActive: true,
          createdAt: '2024-01-01'
        },
        {
          _id: '3',
          name: 'manager',
          displayName: 'Manager',
          description: 'Management level access with reporting capabilities',
          permissions: ['attendance_view', 'reports_view', 'reports_generate', 'section_management'],
          isSystem: false,
          isActive: true,
          createdAt: '2024-02-01'
        },
        {
          _id: '4',
          name: 'employee',
          displayName: 'Employee',
          description: 'Basic employee access',
          permissions: ['attendance_view', 'profile_edit'],
          isSystem: true,
          isActive: true,
          createdAt: '2024-01-01'
        }
      ]);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching roles:', error);
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error for this field
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Handle permission checkbox changes
  const handlePermissionChange = (permissionId) => {
    setFormData(prev => ({
      ...prev,
      permissions: prev.permissions.includes(permissionId)
        ? prev.permissions.filter(p => p !== permissionId)
        : [...prev.permissions, permissionId]
    }));
  };

  // Validate form
  const validateForm = () => {
    const errors = {};
    
    if (!formData.name.trim()) {
      errors.name = 'Role name is required';
    } else if (!/^[a-zA-Z_]+$/.test(formData.name.trim())) {
      errors.name = 'Role name can only contain letters and underscores';
    }
    
    if (!formData.displayName.trim()) {
      errors.displayName = 'Display name is required';
    }
    
    if (formData.permissions.length === 0) {
      errors.permissions = 'At least one permission is required';
    }
    
    return errors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    setSubmitting(true);
    try {
      // Prepare the data
      const submitData = {
        name: formData.name.trim().toLowerCase(),
        displayName: formData.displayName.trim(),
        description: formData.description.trim(),
        permissions: formData.permissions,
        isActive: true
      };

      console.log('Submitting role data:', submitData);
      
      if (currentRole) {
        // Update existing role
        setRoles(roles.map(role => 
          role._id === currentRole._id 
            ? { 
                ...role, 
                ...submitData, 
                updatedAt: new Date().toISOString() 
              }
            : role
        ));
        alert('Role updated successfully!');
      } else {
        // Add new role
        const newRole = {
          ...submitData,
          _id: Date.now().toString(),
          isSystem: false,
          createdAt: new Date().toISOString()
        };
        setRoles([...roles, newRole]);
        alert('Role created successfully!');
      }
      
      handleCloseModal();
    } catch (error) {
      console.error('Error submitting role:', error);
      alert(`Error ${currentRole ? 'updating' : 'adding'} role. Please try again.`);
    } finally {
      setSubmitting(false);
    }
  };

  // Handle opening edit modal
  const handleEdit = (role) => {
    console.log('Editing role:', role);
    setCurrentRole(role);
    setFormData({
      name: role.name || '',
      displayName: role.displayName || '',
      description: role.description || '',
      permissions: role.permissions || []
    });
    setFormErrors({});
    setShowEditModal(true);
  };

  // Handle delete role
  const handleDelete = async (role) => {
    if (role.isSystem) {
      alert('Cannot delete system roles.');
      return;
    }

    if (window.confirm(`Are you sure you want to delete "${role.displayName}" role? This action cannot be undone.`)) {
      try {
        setRoles(roles.filter(r => r._id !== role._id));
        alert('Role deleted successfully!');
      } catch (error) {
        console.error('Error deleting role:', error);
        alert('Error deleting role. Please try again.');
      }
    }
  };

  // Handle toggle role status
  const handleToggleStatus = async (role) => {
    if (role.isSystem) {
      alert('Cannot modify system roles.');
      return;
    }

    try {
      setRoles(roles.map(r => 
        r._id === role._id 
          ? { ...r, isActive: !r.isActive, updatedAt: new Date().toISOString() }
          : r
      ));
      alert(`Role ${!role.isActive ? 'activated' : 'deactivated'} successfully!`);
    } catch (error) {
      console.error('Error toggling role status:', error);
      alert('Error updating role status. Please try again.');
    }
  };

  // Handle opening add modal
  const handleAdd = () => {
    setCurrentRole(null);
    setFormData({ 
      name: '', 
      displayName: '', 
      description: '', 
      permissions: [] 
    });
    setFormErrors({});
    setShowAddModal(true);
  };

  // Handle closing modals
  const handleCloseModal = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setCurrentRole(null);
    setFormData({ 
      name: '', 
      displayName: '', 
      description: '', 
      permissions: [] 
    });
    setFormErrors({});
  };

  // Get permission name by ID
  const getPermissionName = (permissionId) => {
    const permission = availablePermissions.find(p => p.id === permissionId);
    return permission ? permission.name : permissionId;
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="role-access-management">
      {/* Professional Header */}
      <div className="section-header">
        <h2><i className="bi bi-shield-check"></i> Role Access Management</h2>
        <button 
          className="btn-professional btn-primary"
          onClick={handleAdd}
        >
          <i className="bi bi-plus-circle"></i> Add Role
        </button>
      </div>

      {/* Professional Roles Table */}
      <div className="professional-card">
        <div className="table-responsive">
          <table className="professional-table">
            <thead>
              <tr>
                <th>Role Name</th>
                <th>Display Name</th>
                <th>Permissions</th>
                <th>Status</th>
                <th>Type</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {roles.map(role => (
                <tr key={role._id}>
                  <td>
                    <strong>{role.name}</strong>
                    {role.description && (
                      <div style={{ fontSize: '0.85em', color: 'var(--gray-600)', marginTop: '4px' }}>
                        {role.description}
                      </div>
                    )}
                  </td>
                  <td><strong>{role.displayName}</strong></td>
                  <td>
                    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '4px' }}>
                      {role.permissions.slice(0, 3).map(permission => (
                        <span key={permission} className="role-badge role-user" style={{ fontSize: '0.75em' }}>
                          {getPermissionName(permission)}
                        </span>
                      ))}
                      {role.permissions.length > 3 && (
                        <span className="role-badge role-manager" style={{ fontSize: '0.75em' }}>
                          +{role.permissions.length - 3} more
                        </span>
                      )}
                    </div>
                  </td>
                  <td>
                    <span className={`status-badge ${role.isActive ? 'status-active' : 'status-inactive'}`}>
                      {role.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </td>
                  <td>
                    <span className={`role-badge ${role.isSystem ? 'role-admin' : 'role-user'}`}>
                      {role.isSystem ? 'System' : 'Custom'}
                    </span>
                  </td>
                  <td>
                    <div style={{ display: 'flex', gap: '8px' }}>
                      <button 
                        className="btn-professional btn-primary"
                        onClick={() => handleEdit(role)}
                        title="Edit Role"
                        style={{ padding: '8px 12px', fontSize: '12px' }}
                        disabled={role.isSystem}
                      >
                        <i className="bi bi-pencil"></i>
                      </button>
                      <button 
                        className={`btn-professional ${role.isActive ? 'btn-warning' : 'btn-success'}`}
                        onClick={() => handleToggleStatus(role)}
                        title={role.isActive ? 'Deactivate Role' : 'Activate Role'}
                        style={{ padding: '8px 12px', fontSize: '12px' }}
                        disabled={role.isSystem}
                      >
                        <i className={`bi ${role.isActive ? 'bi-pause' : 'bi-play'}`}></i>
                      </button>
                      <button 
                        className="btn-professional btn-danger"
                        onClick={() => handleDelete(role)}
                        title="Delete Role"
                        style={{ padding: '8px 12px', fontSize: '12px' }}
                        disabled={role.isSystem}
                      >
                        <i className="bi bi-trash"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {roles.length === 0 && (
          <div className="no-data">
            <p>No roles found. Click "Add Role" to create the first role.</p>
          </div>
        )}
      </div>

      {/* Add/Edit Role Modal */}
      {(showAddModal || showEditModal) && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content professional-form" onClick={(e) => e.stopPropagation()} style={{ maxWidth: '800px', width: '90%' }}>
            <div className="modal-header" style={{ borderBottom: '2px solid var(--gray-200)', paddingBottom: '20px', marginBottom: '24px' }}>
              <h3 style={{ fontSize: '24px', fontWeight: '700', color: 'var(--gray-900)', display: 'flex', alignItems: 'center', gap: '12px' }}>
                <i className={`bi ${currentRole ? "bi-pencil-square" : "bi-plus-circle"}`} style={{ color: 'var(--primary)' }}></i>
                {currentRole ? 'Edit Role' : 'Add New Role'}
              </h3>
              <button 
                className="modal-close btn-professional btn-danger"
                onClick={handleCloseModal}
                style={{ padding: '8px 12px', fontSize: '16px' }}
              >
                <i className="bi bi-x"></i>
              </button>
            </div>
            
            <form onSubmit={handleSubmit} className="modal-body">
              <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
                <div className="form-group">
                  <label className="form-label">Role Name *</label>
                  <input
                    type="text"
                    name="name"
                    className={`form-input ${formErrors.name ? 'error' : ''}`}
                    value={formData.name}
                    onChange={handleInputChange}
                    placeholder="e.g., manager, clerk"
                    required
                    disabled={currentRole?.isSystem}
                  />
                  {formErrors.name && <span className="error-text">{formErrors.name}</span>}
                </div>

                <div className="form-group">
                  <label className="form-label">Display Name *</label>
                  <input
                    type="text"
                    name="displayName"
                    className={`form-input ${formErrors.displayName ? 'error' : ''}`}
                    value={formData.displayName}
                    onChange={handleInputChange}
                    placeholder="e.g., Manager, Data Entry Clerk"
                    required
                  />
                  {formErrors.displayName && <span className="error-text">{formErrors.displayName}</span>}
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Description</label>
                <textarea
                  name="description"
                  className="form-textarea"
                  value={formData.description}
                  onChange={handleInputChange}
                  placeholder="Brief description of this role"
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Permissions *</label>
                <div className="permissions-grid" style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
                  gap: '12px',
                  maxHeight: '300px',
                  overflowY: 'auto',
                  border: '1px solid var(--gray-300)',
                  borderRadius: '8px',
                  padding: '16px',
                  backgroundColor: 'var(--gray-50)'
                }}>
                  {availablePermissions.map(permission => (
                    <label key={permission.id} className="permission-item" style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '8px',
                      padding: '8px',
                      backgroundColor: 'white',
                      borderRadius: '6px',
                      border: '1px solid var(--gray-200)',
                      cursor: 'pointer'
                    }}>
                      <input
                        type="checkbox"
                        checked={formData.permissions.includes(permission.id)}
                        onChange={() => handlePermissionChange(permission.id)}
                        style={{ marginRight: '8px' }}
                      />
                      <div>
                        <div style={{ fontWeight: '500', fontSize: '14px' }}>
                          {permission.name}
                        </div>
                        <div style={{ fontSize: '12px', color: 'var(--gray-600)' }}>
                          {permission.description}
                        </div>
                      </div>
                    </label>
                  ))}
                </div>
                {formErrors.permissions && <span className="error-text">{formErrors.permissions}</span>}
              </div>

              <div className="modal-footer" style={{ borderTop: '2px solid var(--gray-200)', paddingTop: '20px', marginTop: '24px', display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
                <button 
                  type="button"
                  className="btn-professional btn-secondary"
                  onClick={handleCloseModal}
                  disabled={submitting}
                  style={{ background: 'var(--gray-500)' }}
                >
                  Cancel
                </button>
                <button 
                  type="submit"
                  className="btn-professional btn-success"
                  disabled={submitting}
                >
                  <i className={`bi ${currentRole ? "bi-check-circle" : "bi-plus-circle"}`}></i>
                  {submitting ? (
                    currentRole ? 'Updating...' : 'Adding...'
                  ) : (
                    currentRole ? 'Update Role' : 'Add Role'
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoleAccessManagement;
