import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import logo from '../../assets/logo.jpg';

const Login = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [validationErrors, setValidationErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [loginAttempts, setLoginAttempts] = useState(0);
  const [isBlocked, setIsBlocked] = useState(false);
  const [blockTimeLeft, setBlockTimeLeft] = useState(0);
  const [videoLoaded, setVideoLoaded] = useState(false);
  const { login } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  useEffect(() => {
    // Add the CSS animations to the document
    const style = document.createElement('style');
    style.textContent = `
      @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@200;400;500;600;700;800;900&display=swap');
      @import url('https://fonts.googleapis.com/css2?family=Roboto+Slab:wght@400;700;900&display=swap');

      @keyframes slideInUp {
        from {
          opacity: 0;
          transform: translateY(20px);
        }
        to {
          opacity: 1;
          transform: translateY(0);
        }
      }

      @keyframes float {
        0%, 100% { transform: translateY(0px); }
        50% { transform: translateY(-10px); }
      }

      @keyframes colorChange {
        0% { 
          color: #ffffff;
          text-shadow: 
            0 0 30px rgba(255, 255, 255, 0.8),
            0 4px 15px rgba(0, 0, 0, 0.6),
            0 0 10px rgba(255, 255, 255, 0.5);
        }
        25% { 
          color: #cccccc;
          text-shadow: 
            0 0 25px rgba(204, 204, 204, 0.7),
            0 4px 12px rgba(0, 0, 0, 0.5),
            0 0 8px rgba(255, 255, 255, 0.3);
        }
        50% { 
          color: #000000;
          text-shadow: 
            0 0 20px rgba(255, 255, 255, 0.9),
            0 4px 10px rgba(255, 255, 255, 0.7),
            0 0 15px rgba(255, 255, 255, 0.6);
        }
        75% { 
          color: #333333;
          text-shadow: 
            0 0 25px rgba(255, 255, 255, 0.8),
            0 4px 12px rgba(255, 255, 255, 0.6),
            0 0 12px rgba(255, 255, 255, 0.4);
        }
        100% { 
          color: #ffffff;
          text-shadow: 
            0 0 30px rgba(255, 255, 255, 0.8),
            0 4px 15px rgba(0, 0, 0, 0.6),
            0 0 10px rgba(255, 255, 255, 0.5);
        }
      }

      @keyframes underlineColorChange {
        0% { 
          background: linear-gradient(90deg, transparent, #ffffff 15%, #f0f0f0 30%, #ffffff 50%, #f0f0f0 70%, #ffffff 85%, transparent);
          opacity: 0.8; 
          transform: translateX(-50%) scaleX(0.9); 
        }
        25% { 
          background: linear-gradient(90deg, transparent, #cccccc 15%, #999999 30%, #666666 50%, #999999 70%, #cccccc 85%, transparent);
          opacity: 0.9; 
          transform: translateX(-50%) scaleX(1.0); 
        }
        50% { 
          background: linear-gradient(90deg, transparent, #666666 15%, #333333 30%, #000000 50%, #333333 70%, #666666 85%, transparent);
          opacity: 1.0; 
          transform: translateX(-50%) scaleX(1.1); 
        }
        75% { 
          background: linear-gradient(90deg, transparent, #999999 15%, #666666 30%, #333333 50%, #666666 70%, #999999 85%, transparent);
          opacity: 0.9; 
          transform: translateX(-50%) scaleX(1.0); 
        }
        100% { 
          background: linear-gradient(90deg, transparent, #ffffff 15%, #f0f0f0 30%, #ffffff 50%, #f0f0f0 70%, #ffffff 85%, transparent);
          opacity: 0.8; 
          transform: translateX(-50%) scaleX(0.9); 
        }
      }

      /* Animated gradient background when video doesn't load */
      @keyframes gradientShift {
        0% { background-position: 0% 50%; }
        50% { background-position: 100% 50%; }
        100% { background-position: 0% 50%; }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  // Validation functions
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateEmployeeId = (empId) => {
    // Employee ID should be alphanumeric and between 3-20 characters
    const empIdRegex = /^[A-Za-z0-9]{3,20}$/;
    return empIdRegex.test(empId);
  };

  const validatePassword = (pwd) => {
    // Password should be at least 6 characters
    return pwd && pwd.length >= 6;
  };

  const validateForm = () => {
    const errors = {};
    
    if (!userId.trim()) {
      errors.userId = 'User ID or Email is required';
    } else {
      // Check if it's email or employee ID format
      const isEmail = userId.includes('@');
      if (isEmail && !validateEmail(userId)) {
        errors.userId = 'Please enter a valid email address';
      } else if (!isEmail && !validateEmployeeId(userId)) {
        errors.userId = 'Employee ID must be 3-20 alphanumeric characters';
      }
    }

    if (!password) {
      errors.password = 'Password is required';
    } else if (!validatePassword(password)) {
      errors.password = 'Password must be at least 6 characters';
    }

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Block user after multiple failed attempts
  const handleFailedLogin = () => {
    const newAttempts = loginAttempts + 1;
    setLoginAttempts(newAttempts);
    
    if (newAttempts >= 5) {
      setIsBlocked(true);
      setBlockTimeLeft(300); // 5 minutes block
      
      // Start countdown timer
      const timer = setInterval(() => {
        setBlockTimeLeft(prev => {
          if (prev <= 1) {
            clearInterval(timer);
            setIsBlocked(false);
            setLoginAttempts(0);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Check if user is blocked
    if (isBlocked) {
      setError(`Too many failed attempts. Please wait ${Math.ceil(blockTimeLeft / 60)} minutes before trying again.`);
      return;
    }

    // Clear previous errors
    setError('');
    setValidationErrors({});
    
    // Validate form
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    
    try {
      // Determine if userId is email or employee ID
      const isEmail = userId.includes('@');
      const loginData = isEmail 
        ? { email: userId.trim().toLowerCase(), password }
        : { employeeId: userId.trim().toUpperCase(), password };

      const response = await login(loginData);
      
      // Reset attempts on successful login
      setLoginAttempts(0);
      setIsBlocked(false);
      
      // Check if user is active
      if (response.user && !response.user.isActive) {
        setError('Your account has been deactivated. Please contact your administrator.');
        return;
      }

      // Redirect to dashboard
      navigate('/dashboard');
      
    } catch (err) {
      console.error('Login error:', err);
      
      // Handle different types of errors
      const errorMessage = err.response?.data?.message || err.message || 'Login failed';
      
      if (errorMessage.includes('locked') || errorMessage.includes('too many attempts')) {
        setError('Account temporarily locked due to multiple failed login attempts.');
        handleFailedLogin();
      } else if (errorMessage.includes('inactive') || errorMessage.includes('deactivated')) {
        setError('Your account has been deactivated. Please contact your administrator.');
      } else if (errorMessage.includes('invalid') || errorMessage.includes('credentials')) {
        setError('Invalid credentials. Please check your User ID/Email and password.');
        handleFailedLogin();
      } else if (errorMessage.includes('not found')) {
        setError('User not found. Please check your User ID or Email.');
      } else {
        setError(errorMessage);
        handleFailedLogin();
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      position: 'relative',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      overflow: 'hidden',
      fontFamily: "'Poppins', 'Arial', sans-serif",
      background: videoLoaded 
        ? 'transparent' 
        : `
          linear-gradient(45deg, #667eea 0%, #764ba2 25%, #4568dc 50%, #b06ab3 75%, #667eea 100%),
          radial-gradient(circle at 20% 50%, rgba(120, 119, 198, 0.3) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.3) 0%, transparent 50%),
          radial-gradient(circle at 40% 80%, rgba(120, 219, 226, 0.3) 0%, transparent 50%)
        `,
      backgroundSize: videoLoaded ? 'auto' : '400% 400%, 100% 100%, 100% 100%, 100% 100%',
      animation: videoLoaded ? 'none' : 'gradientShift 15s ease infinite',
      padding: '20px'
    }}>
      {/* Video Background - Try multiple sources */}
      <video 
        autoPlay 
        muted 
        loop 
        playsInline
        style={{ 
          position: 'fixed',
          top: 0, 
          left: 0, 
          width: '100vw',
          height: '100vh',
          objectFit: 'cover', 
          zIndex: videoLoaded ? -2 : -4,
          opacity: videoLoaded ? 0.7 : 0,
          transition: 'opacity 1s ease-in-out'
        }}
        onLoadedData={() => {
          console.log('Video loaded successfully');
          setVideoLoaded(true);
        }}
        onError={(e) => {
          console.log('Video failed to load, using animated background');
          setVideoLoaded(false);
        }}
        onCanPlay={() => {
          console.log('Video can play');
          setVideoLoaded(true);
        }}
      >
        {/* Try multiple video sources */}
        <source src="/assets/background-video.mp4" type="video/mp4" />
        <source src="./assets/background-video.mp4" type="video/mp4" />
        <source src="/public/assets/background-video.mp4" type="video/mp4" />
        <source src="/assets/background-video.webm" type="video/webm" />
        <source src="./assets/background-video.webm" type="video/webm" />
        {/* Fallback for browsers that don't support video */}
        Your browser does not support the video tag.
      </video>

      {/* Video Overlay */}
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.4), rgba(118, 75, 162, 0.4))',
        zIndex: -1,
        backdropFilter: 'blur(2px)'
      }}></div>

      {/* Login Form Box */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.15)',
        padding: '40px',
        borderRadius: '20px',
        boxShadow: `
          0 25px 45px rgba(0, 0, 0, 0.2),
          inset 0 1px 0 rgba(255, 255, 255, 0.4),
          inset 0 -1px 0 rgba(0, 0, 0, 0.1)
        `,
        width: '100%',
        maxWidth: '900px',
        backdropFilter: 'blur(15px)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        transition: 'all 0.3s ease',
        position: 'relative',
        overflow: 'hidden',
        opacity: 1,
        transform: 'translateY(0)',
        animation: 'slideInUp 0.8s ease-out 0.1s forwards, float 6s ease-in-out 1s infinite'
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = 'translateY(-5px)';
        e.currentTarget.style.boxShadow = `
          0 35px 60px rgba(0, 0, 0, 0.3),
          inset 0 1px 0 rgba(255, 255, 255, 0.5),
          inset 0 -1px 0 rgba(0, 0, 0, 0.1)
        `;
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = 'translateY(0)';
        e.currentTarget.style.boxShadow = `
          0 25px 45px rgba(0, 0, 0, 0.2),
          inset 0 1px 0 rgba(255, 255, 255, 0.4),
          inset 0 -1px 0 rgba(0, 0, 0, 0.1)
        `;
      }}
      >
        {/* Shimmer effect */}
        <div style={{
          content: '',
          position: 'absolute',
          top: 0,
          left: '-100%',
          width: '100%',
          height: '100%',
          background: 'linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent)',
          transition: 'left 0.5s'
        }}></div>

        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '40px'
        }}>
          {/* Left Side - Logo and Title */}
          <div style={{
            flex: 1,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center'
          }}>
            <div style={{ textAlign: 'center', marginBottom: '30px', position: 'relative' }}>
              <img 
                src={logo} 
                alt="Logo" 
                style={{
                  maxWidth: '150px',
                  height: 'auto',
                  borderRadius: '50%',
                  boxShadow: `
                    0 15px 40px rgba(0, 0, 0, 0.4),
                    0 0 30px rgba(255, 255, 255, 0.2)
                  `,
                  transition: 'all 0.3s ease',
                  border: '3px solid rgba(255, 255, 255, 0.3)'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.15)';
                  e.target.style.boxShadow = `
                    0 20px 50px rgba(0, 0, 0, 0.5),
                    0 0 40px rgba(255, 255, 255, 0.4)
                  `;
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.5)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'scale(1)';
                  e.target.style.boxShadow = `
                    0 15px 40px rgba(0, 0, 0, 0.4),
                    0 0 30px rgba(255, 255, 255, 0.2)
                  `;
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.3)';
                }}
              />
            </div>
            
            <h1 style={{
              textAlign: 'center',
              fontSize: '48px',
              fontWeight: '900',
              marginBottom: '40px',
              textShadow: `
                0 0 30px rgba(255, 255, 255, 0.8),
                0 4px 15px rgba(0, 0, 0, 0.6),
                0 0 10px rgba(255, 255, 255, 0.5)
              `,
              letterSpacing: '8px',
              textTransform: 'uppercase',
              position: 'relative',
              animation: 'colorChange 4s ease-in-out infinite',
              fontFamily: "'Roboto Slab', serif",
              fontStyle: 'normal',
              lineHeight: '1'
            }}>
              LOGIN
              <div style={{
                content: '',
                position: 'absolute',
                bottom: '-18px',
                left: '50%',
                transform: 'translateX(-50%)',
                width: '140px',
                height: '6px',
                background: 'linear-gradient(90deg, transparent, #ffffff 15%, #cccccc 30%, #000000 50%, #cccccc 70%, #ffffff 85%, transparent)',
                borderRadius: '3px',
                boxShadow: `
                  0 0 25px rgba(255, 255, 255, 0.7),
                  0 3px 10px rgba(0, 0, 0, 0.3)
                `,
                animation: 'underlineColorChange 4s ease-in-out infinite'
              }}></div>
            </h1>
          </div>

          {/* Right Side - Form */}
          <div style={{ flex: 1 }}>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
              {/* Error display */}
              {error && (
                <div style={{
                  background: 'rgba(255, 99, 132, 0.15)',
                  color: '#ff6384',
                  padding: '14px',
                  borderRadius: '10px',
                  textAlign: 'center',
                  fontWeight: '600',
                  fontSize: '14px',
                  border: '1px solid rgba(255, 99, 132, 0.3)',
                  backdropFilter: 'blur(10px)',
                  textShadow: '0 1px 3px rgba(0, 0, 0, 0.3)',
                  animation: 'slideInUp 0.3s ease-out'
                }}>
                  <i className="bi bi-exclamation-triangle" style={{ marginRight: '8px' }}></i>
                  {error}
                </div>
              )}

              {/* Login attempts warning */}
              {loginAttempts > 0 && loginAttempts < 5 && (
                <div style={{
                  background: 'rgba(255, 193, 7, 0.15)',
                  color: '#ffc107',
                  padding: '12px',
                  borderRadius: '10px',
                  textAlign: 'center',
                  fontWeight: '600',
                  fontSize: '13px',
                  border: '1px solid rgba(255, 193, 7, 0.3)',
                  backdropFilter: 'blur(10px)'
                }}>
                  <i className="bi bi-shield-exclamation" style={{ marginRight: '8px' }}></i>
                  {5 - loginAttempts} attempts remaining before account lock
                </div>
              )}

              {/* Account blocked warning */}
              {isBlocked && (
                <div style={{
                  background: 'rgba(220, 53, 69, 0.15)',
                  color: '#dc3545',
                  padding: '14px',
                  borderRadius: '10px',
                  textAlign: 'center',
                  fontWeight: '600',
                  fontSize: '14px',
                  border: '1px solid rgba(220, 53, 69, 0.3)',
                  backdropFilter: 'blur(10px)'
                }}>
                  <i className="bi bi-lock-fill" style={{ marginRight: '8px' }}></i>
                  Account temporarily locked. Try again in {Math.ceil(blockTimeLeft / 60)} minutes.
                </div>
              )}

              {/* User ID Field */}
              <div style={{ position: 'relative', marginBottom: '10px' }}>
                <label style={{
                  display: 'block',
                  color: '#e8f4fd',
                  fontWeight: '700',
                  marginBottom: '10px',
                  fontSize: '15px',
                  textShadow: '0 2px 8px rgba(0, 0, 0, 0.4)',
                  textTransform: 'uppercase',
                  letterSpacing: '1px'
                }}>
                  <i className="bi bi-person-circle" style={{ 
                    marginRight: '10px', 
                    opacity: '0.9', 
                    color: '#c3e9ff',
                    filter: 'drop-shadow(0 1px 3px rgba(0, 0, 0, 0.3))'
                  }}></i>
                  USER ID / EMAIL
                </label>
                <input
                  type="text"
                  placeholder="Enter your Employee ID or Email"
                  value={userId}
                  onChange={(e) => {
                    setUserId(e.target.value);
                    // Clear validation error when user starts typing
                    if (validationErrors.userId) {
                      setValidationErrors(prev => ({ ...prev, userId: '' }));
                    }
                  }}
                  required
                  autoFocus
                  disabled={isBlocked}
                  style={{
                    width: '100%',
                    padding: '16px 22px',
                    border: `2px solid ${validationErrors.userId ? 'rgba(255, 99, 132, 0.5)' : 'rgba(232, 244, 253, 0.3)'}`,
                    borderRadius: '12px',
                    outline: 'none',
                    background: isBlocked ? 'rgba(255, 255, 255, 0.05)' : 'rgba(255, 255, 255, 0.08)',
                    color: isBlocked ? '#999' : '#ffffff',
                    fontSize: '16px',
                    fontWeight: '500',
                    transition: 'all 0.3s ease',
                    backdropFilter: 'blur(10px)',
                    opacity: isBlocked ? 0.6 : 1
                  }}
                  onFocus={(e) => {
                    if (!isBlocked) {
                      e.target.style.borderColor = validationErrors.userId ? 'rgba(255, 99, 132, 0.7)' : 'rgba(232, 244, 253, 0.8)';
                      e.target.style.background = 'rgba(255, 255, 255, 0.15)';
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = `
                        0 10px 25px rgba(0, 0, 0, 0.2),
                        0 0 20px rgba(232, 244, 253, 0.3)
                      `;
                    }
                  }}
                  onBlur={(e) => {
                    if (!isBlocked) {
                      e.target.style.borderColor = validationErrors.userId ? 'rgba(255, 99, 132, 0.5)' : 'rgba(232, 244, 253, 0.3)';
                      e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = 'none';
                    }
                  }}
                />
                {validationErrors.userId && (
                  <div style={{
                    color: '#ff6384',
                    fontSize: '12px',
                    marginTop: '5px',
                    fontWeight: '500',
                    animation: 'slideInUp 0.2s ease-out'
                  }}>
                    <i className="bi bi-exclamation-circle" style={{ marginRight: '5px' }}></i>
                    {validationErrors.userId}
                  </div>
                )}
              </div>

              {/* Password Field */}
              <div style={{ position: 'relative', marginBottom: '10px' }}>
                <label style={{
                  display: 'block',
                  color: '#e8f4fd',
                  fontWeight: '700',
                  marginBottom: '10px',
                  fontSize: '15px',
                  textShadow: '0 2px 8px rgba(0, 0, 0, 0.4)',
                  textTransform: 'uppercase',
                  letterSpacing: '1px'
                }}>
                  <i className="bi bi-shield-lock" style={{ 
                    marginRight: '10px', 
                    opacity: '0.9', 
                    color: '#c3e9ff',
                    filter: 'drop-shadow(0 1px 3px rgba(0, 0, 0, 0.3))'
                  }}></i>
                  PASSWORD
                </label>
                <div style={{ position: 'relative' }}>
                  <input
                    type={showPassword ? 'text' : 'password'}
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => {
                      setPassword(e.target.value);
                      // Clear validation error when user starts typing
                      if (validationErrors.password) {
                        setValidationErrors(prev => ({ ...prev, password: '' }));
                      }
                    }}
                    required
                    disabled={isBlocked}
                    minLength={6}
                    style={{
                      width: '100%',
                      padding: '16px 55px 16px 22px',
                      border: `2px solid ${validationErrors.password ? 'rgba(255, 99, 132, 0.5)' : 'rgba(232, 244, 253, 0.3)'}`,
                      borderRadius: '12px',
                      outline: 'none',
                      background: isBlocked ? 'rgba(255, 255, 255, 0.05)' : 'rgba(255, 255, 255, 0.08)',
                      color: isBlocked ? '#999' : '#ffffff',
                      fontSize: '16px',
                      fontWeight: '500',
                      transition: 'all 0.3s ease',
                      backdropFilter: 'blur(10px)',
                      opacity: isBlocked ? 0.6 : 1
                    }}
                    onFocus={(e) => {
                      if (!isBlocked) {
                        e.target.style.borderColor = validationErrors.password ? 'rgba(255, 99, 132, 0.7)' : 'rgba(232, 244, 253, 0.8)';
                        e.target.style.background = 'rgba(255, 255, 255, 0.15)';
                        e.target.style.transform = 'translateY(-2px)';
                        e.target.style.boxShadow = `
                          0 10px 25px rgba(0, 0, 0, 0.2),
                          0 0 20px rgba(232, 244, 253, 0.3)
                        `;
                      }
                    }}
                    onBlur={(e) => {
                      if (!isBlocked) {
                        e.target.style.borderColor = validationErrors.password ? 'rgba(255, 99, 132, 0.5)' : 'rgba(232, 244, 253, 0.3)';
                        e.target.style.background = 'rgba(255, 255, 255, 0.08)';
                        e.target.style.transform = 'translateY(0)';
                        e.target.style.boxShadow = 'none';
                      }
                    }}
                  />
                  {/* Password toggle button */}
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    disabled={isBlocked}
                    style={{
                      position: 'absolute',
                      right: '15px',
                      top: '50%',
                      transform: 'translateY(-50%)',
                      background: 'transparent',
                      border: 'none',
                      color: isBlocked ? '#666' : '#c3e9ff',
                      fontSize: '18px',
                      cursor: isBlocked ? 'not-allowed' : 'pointer',
                      opacity: isBlocked ? 0.5 : 0.8,
                      transition: 'opacity 0.3s ease'
                    }}
                    onMouseEnter={(e) => {
                      if (!isBlocked) e.target.style.opacity = '1';
                    }}
                    onMouseLeave={(e) => {
                      if (!isBlocked) e.target.style.opacity = '0.8';
                    }}
                  >
                    <i className={`bi ${showPassword ? 'bi-eye-slash' : 'bi-eye'}`}></i>
                  </button>
                </div>
                {validationErrors.password && (
                  <div style={{
                    color: '#ff6384',
                    fontSize: '12px',
                    marginTop: '5px',
                    fontWeight: '500',
                    animation: 'slideInUp 0.2s ease-out'
                  }}>
                    <i className="bi bi-exclamation-circle" style={{ marginRight: '5px' }}></i>
                    {validationErrors.password}
                  </div>
                )}
                
                {/* Password strength indicator */}
                {password && (
                  <div style={{ marginTop: '8px' }}>
                    <div style={{
                      fontSize: '11px',
                      color: '#c3e9ff',
                      marginBottom: '4px',
                      opacity: 0.8
                    }}>
                      Password Strength:
                    </div>
                    <div style={{
                      width: '100%',
                      height: '3px',
                      background: 'rgba(255, 255, 255, 0.1)',
                      borderRadius: '2px',
                      overflow: 'hidden'
                    }}>
                      <div style={{
                        height: '100%',
                        width: `${Math.min((password.length / 12) * 100, 100)}%`,
                        background: password.length < 6 ? '#ff6384' : 
                                  password.length < 8 ? '#ffc107' : '#28a745',
                        transition: 'all 0.3s ease',
                        borderRadius: '2px'
                      }}></div>
                    </div>
                  </div>
                )}
              </div>

              {/* Submit Button */}
              <div style={{ marginTop: '25px' }}>
                <button
                  type="submit"
                  disabled={loading || isBlocked}
                  style={{
                    width: '100%',
                    padding: '18px',
                    fontSize: '18px',
                    fontWeight: '800',
                    color: isBlocked ? '#999' : '#ffffff',
                    border: 'none',
                    borderRadius: '12px',
                    background: isBlocked 
                      ? 'linear-gradient(135deg, #666 0%, #555 50%, #444 100%)'
                      : loading 
                        ? 'linear-gradient(135deg, #888 0%, #666 50%, #555 100%)'
                        : 'linear-gradient(135deg, #4a90e2 0%, #357abd 50%, #2c5aa0 100%)',
                    cursor: loading || isBlocked ? 'not-allowed' : 'pointer',
                    transition: 'all 0.3s ease',
                    textTransform: 'uppercase',
                    letterSpacing: '2px',
                    boxShadow: loading || isBlocked 
                      ? 'none' 
                      : `
                        0 8px 25px rgba(74, 144, 226, 0.4),
                        inset 0 1px 0 rgba(255, 255, 255, 0.2)
                      `,
                    textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
                    opacity: isBlocked ? 0.6 : 1
                  }}
                  onMouseEnter={(e) => {
                    if (!loading && !isBlocked) {
                      e.target.style.transform = 'translateY(-3px)';
                      e.target.style.boxShadow = `
                        0 12px 35px rgba(74, 144, 226, 0.6),
                        inset 0 1px 0 rgba(255, 255, 255, 0.3)
                      `;
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (!loading && !isBlocked) {
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = `
                        0 8px 25px rgba(74, 144, 226, 0.4),
                        inset 0 1px 0 rgba(255, 255, 255, 0.2)
                      `;
                    }
                  }}
                >
                  {loading ? (
                    <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
                      <i className="bi bi-arrow-clockwise" style={{ 
                        animation: 'spin 1s linear infinite', 
                        fontSize: '16px' 
                      }}></i>
                      Authenticating...
                    </span>
                  ) : isBlocked ? (
                    <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
                      <i className="bi bi-lock-fill"></i>
                      Account Locked
                    </span>
                  ) : (
                    <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
                      <i className="bi bi-box-arrow-in-right"></i>
                      Secure Login
                    </span>
                  )}
                </button>
              </div>

              {/* Security Notice */}
              <div style={{
                marginTop: '20px',
                padding: '12px',
                background: 'rgba(32, 201, 151, 0.1)',
                border: '1px solid rgba(32, 201, 151, 0.3)',
                borderRadius: '8px',
                textAlign: 'center',
                fontSize: '12px',
                color: '#20c997',
                fontWeight: '500'
              }}>
                <i className="bi bi-shield-check" style={{ marginRight: '6px' }}></i>
                Your login is secured with advanced encryption
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
