import React, { useState, useEffect } from 'react';
import { Doughnut, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  BarElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  BarElement
);

const DashboardStats = () => {
  const [stats, setStats] = useState({
    totalEmployees: 12960,
    presentToday: 0,
    attendanceRate: 0,
    activeUsers: 1,
    totalUsers: 6,
    loginRate: 16.7,
    activeDivisions: 27,
    avgPerDivision: 480
  });

  // Fetch dashboard stats from API
  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Replace with actual API calls
        const response = await fetch('/api/dashboard/stats');
        if (response.ok) {
          const data = await response.json();
          setStats(prevStats => ({
            ...prevStats,
            ...data
          }));
        }
      } catch (error) {
        console.error('Error fetching dashboard stats:', error);
        // Keep default/mock data on error
      }
    };

    fetchStats();
    
    // Set up interval to refresh stats every 5 minutes
    const interval = setInterval(fetchStats, 5 * 60 * 1000);
    
    return () => clearInterval(interval);
  }, []);

  // Mock data - replace with actual API calls
  const userActivityData = {
    labels: ['Active', 'Inactive'],
    datasets: [
      {
        data: [stats.activeUsers, stats.totalUsers - stats.activeUsers],
        backgroundColor: ['#4F94CD', '#B0C4DE'],
        borderWidth: 0,
      },
    ],
  };

  const divisionDistributionData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Employee Count',
        data: [3500, 3200, 3800, 3300, 3600, 3400],
        backgroundColor: '#4CAF50',
        borderRadius: 4,
      },
    ],
  };

  return (
    <div className="dashboard-stats">
      <div className="stats-grid">
        {/* Total Employees Card */}
        <div className="stat-card gradient-blue">
          <div className="stat-header">
            <h3>TOTAL EMPLOYEES</h3>
            <i className="bi bi-people stat-icon"></i>
          </div>
          <div className="stat-number">{stats.totalEmployees.toLocaleString()}</div>
          <div className="stat-label">Registered</div>
        </div>

        {/* Present Today Card */}
        <div className="stat-card gradient-green">
          <div className="stat-header">
            <h3>PRESENT TODAY</h3>
            <i className="bi bi-person-check stat-icon"></i>
          </div>
          <div className="stat-number">{stats.presentToday}</div>
          <div className="stat-label">+{stats.attendanceRate}% Attendance</div>
        </div>

        {/* User Activity Card */}
        <div className="stat-card">
          <div className="card-header">
            <h3><i className="bi bi-people"></i> USER ACTIVITY</h3>
            <p>System login activity comparison</p>
          </div>
          <div className="activity-stats">
            <div className="activity-item">
              <div className="activity-number">{stats.activeUsers}</div>
              <div className="activity-label">ACTIVE TODAY</div>
            </div>
            <div className="activity-item">
              <div className="activity-number">{stats.totalUsers}</div>
              <div className="activity-label">TOTAL USERS</div>
            </div>
            <div className="activity-item">
              <div className="activity-number">{stats.loginRate}%</div>
              <div className="activity-label">LOGIN RATE</div>
            </div>
          </div>
          <div className="chart-container">
            <Doughnut 
              data={userActivityData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    display: false
                  }
                }
              }}
            />
          </div>
        </div>

        {/* Division Distribution Card */}
        <div className="stat-card">
          <div className="card-header">
            <h3><i className="bi bi-building"></i> DIVISION DISTRIBUTION</h3>
            <p>Active employee count per department</p>
          </div>
          <div className="division-stats">
            <div className="division-item">
              <div className="division-number">{stats.activeDivisions}</div>
              <div className="division-label">ACTIVE DIVISIONS</div>
            </div>
            <div className="division-item">
              <div className="division-number">{stats.totalEmployees.toLocaleString()}</div>
              <div className="division-label">TOTAL EMPLOYEES</div>
            </div>
            <div className="division-item">
              <div className="division-number">{stats.avgPerDivision}</div>
              <div className="division-label">AVG/DIVISION</div>
            </div>
          </div>
          <div className="chart-container">
            <Bar 
              data={divisionDistributionData} 
              options={{
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                  legend: {
                    display: false
                  }
                },
                scales: {
                  y: {
                    beginAtZero: true,
                    grid: {
                      display: false
                    }
                  },
                  x: {
                    grid: {
                      display: false
                    }
                  }
                }
              }}
            />
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button className="action-btn">
            <i className="bi bi-person-plus"></i>
            Add User
          </button>
          <button className="action-btn">
            <i className="bi bi-file-earmark-text"></i>
            Generate Report
          </button>
          <button className="action-btn">
            <i className="bi bi-building"></i>
            Manage Divisions
          </button>
          <button className="action-btn">
            <i className="bi bi-gear"></i>
            Settings
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardStats;
