import React, { useState, useEffect } from 'react';

const DivisionManagement = () => {
  const [divisions, setDivisions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [currentDivision, setCurrentDivision] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    code: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);

  // Fetch divisions from API
  const fetchDivisions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/divisions', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setDivisions(data.data || []);
      } else {
        console.error('Failed to fetch divisions');
        // Fallback to mock data if API fails
        setDivisions([
          { _id: '1', name: 'Engineering', code: 'ENG', employeeCount: 120 },
          { _id: '2', name: 'Human Resources', code: 'HR', employeeCount: 25 },
          { _id: '3', name: 'Finance', code: 'FIN', employeeCount: 35 },
          { _id: '4', name: 'Marketing', code: 'MKT', employeeCount: 18 },
          { _id: '5', name: 'Operations', code: 'OPS', employeeCount: 45 }
        ]);
      }
    } catch (error) {
      console.error('Error fetching divisions:', error);
      // Fallback to mock data
      setDivisions([
        { _id: '1', name: 'Engineering', code: 'ENG', employeeCount: 120 },
        { _id: '2', name: 'Human Resources', code: 'HR', employeeCount: 25 },
        { _id: '3', name: 'Finance', code: 'FIN', employeeCount: 35 },
        { _id: '4', name: 'Marketing', code: 'MKT', employeeCount: 18 },
        { _id: '5', name: 'Operations', code: 'OPS', employeeCount: 45 }
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error for this field
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Validate form
  const validateForm = () => {
    const errors = {};
    if (!formData.name.trim()) errors.name = 'Division name is required';
    if (!formData.code.trim()) errors.code = 'Division code is required';
    return errors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    setSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/divisions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        await fetchDivisions(); // Refresh the list
        setShowAddModal(false);
        setFormData({ name: '', code: '' });
        setFormErrors({});
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Failed to add division');
      }
    } catch (error) {
      console.error('Error adding division:', error);
      alert('Error adding division. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  // Use effect to fetch divisions on component mount
  useEffect(() => {
    fetchDivisions();
  }, []);

  if (loading) {
    return <div className="loading-container">Loading...</div>;
  }

  return (
    <div className="division-management">
      <div className="page-header">
        <h2><i className="bi bi-building"></i> Division Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowAddModal(true)}
        >
          <i className="bi bi-plus-circle"></i> Add Division
        </button>
      </div>

      <div className="divisions-grid">
        {divisions.map(division => (
          <div key={division._id} className="division-card">
            <div className="division-header">
              <h4>{division.name}</h4>
              <div className="division-code">
                <i className="bi bi-tag-fill"></i>
                {division.code}
              </div>
            </div>
            
            <div className="division-stats">
              <div className="stat-item">
                <div className="stat-icon">
                  <i className="bi bi-people-fill"></i>
                </div>
                <div className="stat-content">
                  <span className="stat-number">{division.employeeCount || 0}</span>
                  <span className="stat-label">Employees</span>
                </div>
              </div>
              
              <div className="stat-item">
                <div className="stat-icon success">
                  <i className="bi bi-check-circle-fill"></i>
                </div>
                <div className="stat-content">
                  <span className="stat-number">Active</span>
                  <span className="stat-label">Status</span>
                </div>
              </div>
            </div>
            
            <div className="actions">
              <button className="btn btn-sm btn-outline-primary">
                <i className="bi bi-pencil-square"></i> Edit
              </button>
              <button className="btn btn-sm btn-outline-danger">
                <i className="bi bi-trash3"></i> Delete
              </button>
            </div>
          </div>
        ))}
        
        {/* Add New Division Card */}
        <div 
          className="division-card add-card"
          onClick={() => setShowAddModal(true)}
        >
          <div className="add-content">
            <div className="add-icon">
              <i className="bi bi-plus-circle"></i>
            </div>
            <h4>Add New Division</h4>
            <p>Create a new division to organize your workforce</p>
          </div>
        </div>
      </div>

      {/* Add Division Modal */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3><i className="bi bi-plus-circle"></i> Add New Division</h3>
              <button 
                className="close-btn"
                onClick={() => setShowAddModal(false)}
              >
                <i className="bi bi-x"></i>
              </button>
            </div>
            
            <form onSubmit={handleSubmit} className="division-form">
              <div className="form-group">
                <label htmlFor="name">Division Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={formErrors.name ? 'error' : ''}
                  placeholder="Enter division name"
                />
                {formErrors.name && <span className="error-text">{formErrors.name}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="code">Division Code *</label>
                <input
                  type="text"
                  id="code"
                  name="code"
                  value={formData.code}
                  onChange={handleInputChange}
                  className={formErrors.code ? 'error' : ''}
                  placeholder="Enter division code (e.g., ENG, HR)"
                />
                {formErrors.code && <span className="error-text">{formErrors.code}</span>}
              </div>

              <div className="modal-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowAddModal(false)}
                  disabled={submitting}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={submitting}
                >
                  {submitting ? (
                    <>
                      <i className="bi bi-hourglass-split"></i> Adding...
                    </>
                  ) : (
                    <>
                      <i className="bi bi-check-circle"></i> Add Division
                    </>
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default DivisionManagement;
