import React, { useState } from 'react';

const ReportGeneration = () => {
  const [reportType, setReportType] = useState('attendance');
  const [reportScope, setReportScope] = useState('individual');
  const [employeeId, setEmployeeId] = useState('');
  const [dateRange, setDateRange] = useState({
    startDate: '',
    endDate: ''
  });
  const [loading, setLoading] = useState(false);
  const [reportData, setReportData] = useState(null);
  const [error, setError] = useState('');

  const generateReport = async () => {
    if (!dateRange.startDate || !dateRange.endDate) {
      setError('Please select both start and end dates');
      return;
    }

    if (reportScope === 'individual' && !employeeId && reportType === 'attendance') {
      setError('Please enter Employee ID for individual reports');
      return;
    }

    setLoading(true);
    setError('');
    setReportData(null);

    try {
      let endpoint = '';
      let requestData = {
        from_date: dateRange.startDate,
        to_date: dateRange.endDate,
        report_type: reportScope
      };

      // Add employee ID for individual reports
      if (reportScope === 'individual' && employeeId) {
        requestData.employee_id = employeeId;
      }

      // Determine endpoint based on report type
      switch (reportType) {
        case 'attendance':
          endpoint = '/api/reports/mysql/attendance';
          break;
        case 'meal':
          endpoint = '/api/reports/mysql/meal';
          break;
        case 'audit':
          endpoint = '/api/reports/audit';
          break;
        default:
          endpoint = '/api/reports/mysql/attendance';
      }

      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setReportData(data);
        console.log('Report generated successfully:', data);
      } else {
        setError(data.message || 'Failed to generate report');
      }

    } catch (err) {
      console.error('Report generation error:', err);
      setError('Network error occurred while generating report');
    } finally {
      setLoading(false);
    }
  };

  const exportReport = (format = 'csv') => {
    if (!reportData || !reportData.data) {
      setError('No data to export');
      return;
    }

    try {
      let content = '';
      let filename = `${reportType}_report_${dateRange.startDate}_to_${dateRange.endDate}`;

      if (format === 'csv') {
        // Generate CSV content
        if (reportType === 'attendance') {
          const headers = ['Attendance ID', 'Employee ID', 'Fingerprint ID', 'Date', 'Time', 'Scan Type'];
          content = headers.join(',') + '\n';
          
          reportData.data.forEach(record => {
            const row = [
              record.attendance_id || '',
              record.employee_ID || '',
              record.fingerprint_id || '',
              record.date_ || '',
              record.time_ || '',
              record.scan_type || ''
            ];
            content += row.join(',') + '\n';
          });
        } else if (reportType === 'meal') {
          const headers = ['ID', 'Employee ID', 'Meal Date', 'Meal Type', 'Booking Time', 'Status'];
          content = headers.join(',') + '\n';
          
          reportData.data.forEach(record => {
            const row = [
              record.id || '',
              record.employee_id || '',
              record.meal_date || '',
              record.meal_type || '',
              record.booking_time || '',
              record.status || ''
            ];
            content += row.join(',') + '\n';
          });
        }

        filename += '.csv';
      } else if (format === 'json') {
        content = JSON.stringify(reportData, null, 2);
        filename += '.json';
      }

      // Create and download file
      const blob = new Blob([content], { type: 'text/plain' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

    } catch (err) {
      console.error('Export error:', err);
      setError('Failed to export report');
    }
  };

  return (
    <div className="report-generation">
      <div className="page-header">
        <h2><i className="bi bi-file-earmark-text"></i> Report Generation</h2>
      </div>

      <div className="report-forms">
        <div className="form-row">
          <div className="form-group">
            <label>Report Type</label>
            <select 
              className="form-control"
              value={reportType}
              onChange={(e) => setReportType(e.target.value)}
            >
              <option value="attendance">Attendance Report</option>
              <option value="meal">Meal Report</option>
              <option value="audit">Audit Report</option>
            </select>
          </div>

          {(reportType === 'attendance' || reportType === 'meal') && (
            <div className="form-group">
              <label>Report Scope</label>
              <select 
                className="form-control"
                value={reportScope}
                onChange={(e) => setReportScope(e.target.value)}
              >
                <option value="individual">Individual Report</option>
                <option value="group">Group Report</option>
              </select>
            </div>
          )}
        </div>

        {reportScope === 'individual' && (reportType === 'attendance' || reportType === 'meal') && (
          <div className="form-group">
            <label>Employee ID</label>
            <input 
              type="text"
              className="form-control"
              placeholder="Enter Employee ID (e.g., SA1001, 006, etc.)"
              value={employeeId}
              onChange={(e) => setEmployeeId(e.target.value)}
            />
          </div>
        )}

        <div className="date-range">
          <div className="form-group">
            <label>Start Date</label>
            <input 
              type="date"
              className="form-control"
              value={dateRange.startDate}
              onChange={(e) => setDateRange({...dateRange, startDate: e.target.value})}
            />
          </div>
          <div className="form-group">
            <label>End Date</label>
            <input 
              type="date"
              className="form-control"
              value={dateRange.endDate}
              onChange={(e) => setDateRange({...dateRange, endDate: e.target.value})}
            />
          </div>
        </div>

        {error && (
          <div className="alert alert-danger">
            <i className="bi bi-exclamation-triangle"></i> {error}
          </div>
        )}

        <div className="button-group">
          <button 
            className="btn btn-success"
            onClick={generateReport}
            disabled={loading}
          >
            {loading ? (
              <>
                <i className="bi bi-arrow-clockwise spin"></i> Generating...
              </>
            ) : (
              <>
                <i className="bi bi-file-earmark-bar-graph"></i> Generate Report
              </>
            )}
          </button>

          {reportData && (
            <>
              <button 
                className="btn btn-primary"
                onClick={() => exportReport('csv')}
              >
                <i className="bi bi-download"></i> Export CSV
              </button>
              <button 
                className="btn btn-secondary"
                onClick={() => exportReport('json')}
              >
                <i className="bi bi-download"></i> Export JSON
              </button>
            </>
          )}
        </div>

        {/* Report Results */}
        {reportData && (
          <div className="report-results">
            <div className="report-summary">
              <h4>Report Summary</h4>
              <div className="summary-stats">
                {reportType === 'attendance' && (
                  <>
                    <div className="stat-item">
                      <span className="stat-label">Total Records:</span>
                      <span className="stat-value">{reportData.summary.total_records}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">Unique Employees:</span>
                      <span className="stat-value">{reportData.summary.unique_employees}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">IN Scans:</span>
                      <span className="stat-value">{reportData.summary.in_scans}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">OUT Scans:</span>
                      <span className="stat-value">{reportData.summary.out_scans}</span>
                    </div>
                  </>
                )}
                
                {reportType === 'meal' && (
                  <>
                    <div className="stat-item">
                      <span className="stat-label">Total Bookings:</span>
                      <span className="stat-value">{reportData.summary.total_bookings}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">Unique Employees:</span>
                      <span className="stat-value">{reportData.summary.unique_employees}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">Breakfast:</span>
                      <span className="stat-value">{reportData.summary.breakfast_bookings}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">Lunch:</span>
                      <span className="stat-value">{reportData.summary.lunch_bookings}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">Dinner:</span>
                      <span className="stat-value">{reportData.summary.dinner_bookings}</span>
                    </div>
                  </>
                )}
              </div>
            </div>

            <div className="report-data">
              <h4>Report Data ({reportData.data.length} records)</h4>
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      {reportType === 'attendance' && (
                        <>
                          <th>Attendance ID</th>
                          <th>Employee ID</th>
                          <th>Fingerprint ID</th>
                          <th>Date</th>
                          <th>Time</th>
                          <th>Scan Type</th>
                        </>
                      )}
                      {reportType === 'meal' && (
                        <>
                          <th>ID</th>
                          <th>Employee ID</th>
                          <th>Meal Date</th>
                          <th>Meal Type</th>
                          <th>Booking Time</th>
                          <th>Status</th>
                        </>
                      )}
                    </tr>
                  </thead>
                  <tbody>
                    {reportData.data.slice(0, 100).map((record, index) => (
                      <tr key={index}>
                        {reportType === 'attendance' && (
                          <>
                            <td>{record.attendance_id}</td>
                            <td>{record.employee_ID}</td>
                            <td>{record.fingerprint_id}</td>
                            <td>{record.date_}</td>
                            <td>{record.time_}</td>
                            <td>
                              <span className={`badge ${record.scan_type?.toUpperCase() === 'IN' ? 'badge-success' : 'badge-danger'}`}>
                                {record.scan_type}
                              </span>
                            </td>
                          </>
                        )}
                        {reportType === 'meal' && (
                          <>
                            <td>{record.id}</td>
                            <td>{record.employee_id}</td>
                            <td>{record.meal_date}</td>
                            <td>{record.meal_type}</td>
                            <td>{record.booking_time}</td>
                            <td>{record.status}</td>
                          </>
                        )}
                      </tr>
                    ))}
                  </tbody>
                </table>
                {reportData.data.length > 100 && (
                  <p className="text-muted">
                    Showing first 100 records. Total: {reportData.data.length} records. 
                    Use export function to download complete data.
                  </p>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReportGeneration;
