import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import DashboardStats from './DashboardStats';
import UserManagement from './UserManagement';
import ReportGeneration from './ReportGeneration';
import DivisionManagement from './DivisionManagement';
import SectionManagement from './SectionManagement';
import RoleAccessManagement from './RoleAccessManagement';
import Settings from './Settings';
import './Dashboard.css';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const { user, logout } = useContext(AuthContext);

  const handleLogout = async () => {
    try {
      await logout();
      window.location.href = '/login';
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleQuickAction = (action) => {
    switch (action) {
      case 'home':
        setActiveSection('dashboard');
        break;
      case 'add-user':
        setActiveSection('users');
        break;
      case 'reports':
        setActiveSection('reports');
        break;
      case 'divisions':
        setActiveSection('divisions');
        break;
      case 'sections':
        setActiveSection('sections');
        break;
      case 'settings':
        setActiveSection('settings');
        break;
      default:
        break;
    }
  };

  const renderActiveSection = () => {
    switch (activeSection) {
      case 'dashboard':
        return <DashboardStats />;
      case 'users':
        return <UserManagement />;
      case 'reports':
      case 'unit-attendance':
      case 'audit-report':
      case 'meal-report':
        return <ReportGeneration />;
      case 'divisions':
        return <DivisionManagement />;
      case 'sections':
        return <SectionManagement />;
      case 'roles':
        return <RoleAccessManagement />;
      case 'settings':
        return <Settings />;
      default:
        return <DashboardStats />;
    }
  };

  const menuItems = [
    {
      id: 'dashboard',
      label: 'Dashboard',
      icon: 'bi-speedometer2',
      roles: ['super_admin', 'admin', 'clerk', 'employee']
    },
    {
      id: 'reports',
      label: 'Reports',
      icon: 'bi-file-earmark-text',
      roles: ['super_admin', 'admin', 'clerk']
    },
    {
      id: 'users',
      label: 'User Management',
      icon: 'bi-people',
      roles: ['super_admin', 'admin']
    },
    {
      id: 'divisions',
      label: 'Manage Divisions',
      icon: 'bi-building',
      roles: ['super_admin']
    },
    {
      id: 'sections',
      label: 'Manage Sections',
      icon: 'bi-diagram-3',
      roles: ['super_admin', 'admin']
    },
    {
      id: 'roles',
      label: 'Role Access',
      icon: 'bi-shield-check',
      roles: ['super_admin']
    },
    {
      id: 'settings',
      label: 'Settings',
      icon: 'bi-gear',
      roles: ['super_admin', 'admin']
    }
  ];

  const quickActions = [
    {
      id: 'home',
      label: 'Home',
      icon: 'bi-house',
      color: 'green',
      roles: ['super_admin', 'admin', 'clerk', 'employee']
    },
    {
      id: 'add-user',
      label: 'Add User',
      icon: 'bi-person-plus',
      color: 'purple',
      roles: ['super_admin', 'admin']
    },
    {
      id: 'reports',
      label: 'Reports',
      icon: 'bi-file-earmark-text',
      color: 'orange',
      roles: ['super_admin', 'admin', 'clerk']
    },
    {
      id: 'divisions',
      label: 'Divisions',
      icon: 'bi-building',
      color: 'dark',
      roles: ['super_admin']
    },
    {
      id: 'sections',
      label: 'Sections',
      icon: 'bi-diagram-3',
      color: 'gold',
      roles: ['super_admin', 'admin']
    },
    {
      id: 'settings',
      label: 'Settings',
      icon: 'bi-gear',
      color: 'gray',
      roles: ['super_admin', 'admin']
    }
  ];

  const hasAccess = (roles) => {
    return roles.includes(user?.role);
  };

  return (
    <div className="dashboard-container-full">
      {/* Top Navigation Bar */}
      <div className="top-navbar">
        <div className="navbar-left">
          <div className="logo-section">
            <i className="bi bi-clock-history"></i>
            <span className="system-name">TIME ATTENDANCE SYSTEM - SLPA</span>
          </div>
        </div>

        <div className="navbar-center">
          <nav className="horizontal-nav">
            {menuItems.map((item) => {
              if (!hasAccess(item.roles)) return null;
              
              return (
                <button
                  key={item.id}
                  className={`nav-btn ${activeSection === item.id ? 'active' : ''}`}
                  onClick={() => setActiveSection(item.id)}
                >
                  <i className={`bi ${item.icon}`}></i>
                  <span>{item.label}</span>
                </button>
              );
            })}
          </nav>
        </div>

        <div className="navbar-right">
          <div className="user-info-top">
            <i className="bi bi-person-circle"></i>
            <span className="user-name">
              {user?.firstName} {user?.lastName} ({user?.role?.replace('_', ' ').toUpperCase()})
            </span>
          </div>
          <button className="logout-btn-top" onClick={handleLogout}>
            <i className="bi bi-box-arrow-right"></i>
            Logout
          </button>
        </div>
      </div>

      {/* Quick Actions Bar */}
      <div className="quick-actions-navbar">
        <div className="quick-actions-container">
          <div className="quick-actions-label">
            <i className="bi bi-lightning"></i>
            Quick Actions:
          </div>
          <div className="quick-actions-buttons">
            {quickActions.map((action) => {
              if (!hasAccess(action.roles)) return null;
              
              return (
                <button
                  key={action.id}
                  className={`quick-action-btn ${action.color}`}
                  onClick={() => handleQuickAction(action.id)}
                  title={action.label}
                >
                  <i className={`bi ${action.icon}`}></i>
                  <span>{action.label}</span>
                </button>
              );
            })}
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="main-content-full">
        <div className="content-wrapper">
          {renderActiveSection()}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
