import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddRole = () => {
  const [roleName, setRoleName] = useState('');
  const [roleDescription, setRoleDescription] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!roleName.trim()) {
      setError('Role name is required.');
      return;
    }
    // TODO: Replace with actual API call to add role
    setSuccess('Role added successfully!');
    setTimeout(() => navigate(-1), 1200);
  };

  return (
    <div className="add-role-wrapper" style={{ maxWidth: 500, margin: '40px auto', background: '#fff', padding: 32, borderRadius: 12, boxShadow: '0 2px 12px #0001' }}>
      <h2 style={{ marginBottom: 24 }}><i className="bi bi-person-plus"></i> Add New Role</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group" style={{ marginBottom: 18 }}>
          <label className="form-label">Role Name *</label>
          <input
            type="text"
            className="form-input"
            value={roleName}
            onChange={e => setRoleName(e.target.value)}
            required
            placeholder="Enter role name"
          />
        </div>
        <div className="form-group" style={{ marginBottom: 18 }}>
          <label className="form-label">Description</label>
          <textarea
            className="form-input"
            value={roleDescription}
            onChange={e => setRoleDescription(e.target.value)}
            placeholder="Enter role description (optional)"
            rows={3}
          />
        </div>
        {error && <div style={{ color: 'red', marginBottom: 12 }}>{error}</div>}
        {success && <div style={{ color: 'green', marginBottom: 12 }}>{success}</div>}
        <div style={{ display: 'flex', gap: 12 }}>
          <button type="submit" className="btn-professional btn-primary">
            <i className="bi bi-plus-circle"></i> Add Role
          </button>
          <button type="button" className="btn-professional btn-secondary" onClick={() => navigate(-1)}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddRole;
