import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddRole = () => {
  const [formData, setFormData] = useState({
    roleName: '',
    description: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!formData.roleName.trim()) {
      setError('Role name is required.');
      return;
    }
    // TODO: Replace with actual API call to add role
    setSuccess('Role added successfully!');
    setTimeout(() => navigate(-1), 1200);
  };

  return (
    <div className="modal-overlay" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', background: 'rgba(0,0,0,0.08)' }}>
      <div className="modal-content professional-form" style={{ minWidth: 400, maxWidth: 500 }}>
        <div className="modal-header" style={{ borderBottom: '2px solid var(--gray-200)', paddingBottom: '20px', marginBottom: '24px' }}>
          <h3 style={{ fontSize: '24px', fontWeight: '700', color: 'var(--gray-900)', display: 'flex', alignItems: 'center', gap: '12px' }}>
            <i className="bi bi-person-plus" style={{ color: 'var(--primary)' }}></i>
            Add New Role
          </h3>
          <button 
            className="modal-close btn-professional btn-danger"
            onClick={() => navigate(-1)}
            style={{ padding: '8px 12px', fontSize: '16px' }}
          >
            <i className="bi bi-x"></i>
          </button>
        </div>
        <form onSubmit={handleSubmit} className="modal-body">
          <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr', gap: '20px' }}>
            <div className="form-group">
              <label className="form-label">Role Name *</label>
              <input
                type="text"
                name="roleName"
                className="form-input"
                value={formData.roleName}
                onChange={handleInputChange}
                required
                placeholder="Enter role name"
              />
            </div>
            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                name="description"
                className="form-input"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="Enter role description (optional)"
                rows={3}
              />
            </div>
          </div>
          {error && <div style={{ color: 'red', marginBottom: 12 }}>{error}</div>}
          {success && <div style={{ color: 'green', marginBottom: 12 }}>{success}</div>}
          <div className="modal-footer" style={{ borderTop: '2px solid var(--gray-200)', paddingTop: '20px', marginTop: '24px', display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
            <button 
              type="button"
              className="btn-professional btn-secondary"
              onClick={() => navigate(-1)}
              style={{ background: 'var(--gray-500)' }}
            >
              Cancel
            </button>
            <button 
              type="submit"
              className="btn-professional btn-success"
            >
              <i className="bi bi-check-circle"></i>
              Add Role
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddRole;
