import React, { useState, useEffect, useContext } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { AuthContext } from '../../context/AuthContext';
import RoleManagement from './RoleManagement';
import './RoleAccessManagement.css';

const RoleAccessManagement = () => {
  const { user } = useContext(AuthContext);
  const isSuperAdmin = user?.role === 'super_admin';

  const [roles, setRoles] = useState([]);
  const [permissionCatalog, setPermissionCatalog] = useState([]);
  const [selectedRole, setSelectedRole] = useState('');
  const [formData, setFormData] = useState({});
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [showRoleManagement, setShowRoleManagement] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [tempPermissions, setTempPermissions] = useState([]);

  const handleRoleChange = (e) => {
    const role = e.target.value;
    setSelectedRole(role);
    setAssignedPermissions([]);
  };

  const handlePermissionToggle = (permission) => {
    setTempPermissions((prev) =>
      prev.includes(permission)
        ? prev.filter((p) => p !== permission)
        : [...prev, permission]
    );
  };

  const handleSave = () => {
    setAssignedPermissions(tempPermissions);
    setShowModal(false);
  };

  const openModal = () => {
    setTempPermissions(assignedPermissions);
    setShowModal(true);
  };

  return (
    <div className="role-access-wrapper">
      <div className="main-card">
        <h3 className="form-header">Role Access Management</h3>

        {/* Select Role */}
        <div className="form-group-modern">
          <label className="form-label-modern">Select Role</label>
          <Form.Select
            value={selectedRole}
            onChange={handleRoleChange}
            className="form-control-modern"
          >
            <option value="">-- Select a Role --</option>
            {roles.map((role, idx) => (
              <option key={idx} value={role}>
                {role}
              </option>
            ))}
          </Form.Select>
        </div>

        {/* Assigned Permissions */}
        {selectedRole && (
          <div className="form-group-modern">
            <label className="form-label-modern">Assigned Permissions</label>
            <div className="assigned-permissions">
              {assignedPermissions.length > 0 ? (
                assignedPermissions.map((perm, idx) => (
                  <span key={idx} className="permission-chip">
                    {perm}
                  </span>
                ))
              ) : (
                <span className="text-muted">No permissions assigned</span>
              )}
            </div>
          </div>
        )}

        {/* Buttons */}
        {selectedRole && (
          <div className="form-actions">
            <Button className="btn-save-modern" onClick={openModal}>
              <i className="bi bi-shield-lock me-2"></i> Manage Access
            </Button>
            <button
              className="btn-manage-roles"
              onClick={() => navigate('/dashboard/role-management')}
            >
              <i
                className="bi bi-gear"
                style={{ marginRight: '6px', fontSize: '1.1rem' }}
              ></i>
              Manage Roles
            </button>
          </div>
        )}
      </div>

      {/* Modal */}
      <Modal
        show={showModal}
        onHide={() => setShowModal(false)}
        centered
        dialogClassName="custom-modal"
      >
        <Modal.Header closeButton>
          <Modal.Title>Manage Access for {selectedRole}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {permissions.map((perm, idx) => (
            <div key={idx} className="permission-item">
              <Form.Check
                type="checkbox"
                label={perm}
                checked={tempPermissions.includes(perm)}
                onChange={() => handlePermissionToggle(perm)}
              />
            </div>
          ))}
        </Modal.Body>
        <Modal.Footer>
          <Button
            variant="secondary"
            className="btn-professional"
            onClick={() => setShowModal(false)}
          >
            Cancel
          </Button>
          <Button
            variant="primary"
            className="btn-save-modern"
            onClick={handleSave}
          >
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default RoleAccessManagement;
