import React, { useState, useEffect } from 'react';
import './MealManagement.css';

const MealManagement = () => {
  const [divisions, setDivisions] = useState([]);
  const [sections, setSections] = useState([]);
  const [allSections, setAllSections] = useState([]);
  const [mealBooking, setMealBooking] = useState({
    divisionId: '',
    sectionId: '',
    mealDate: new Date().toISOString().split('T')[0],
    mealType: '',
    quantity: 1,
    specialRequirements: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [todaysBookings, setTodaysBookings] = useState(0);

  // Fetch divisions and sections on component mount
  useEffect(() => {
    fetchDivisions();
    fetchAllSections();
    fetchTodaysBookingsCount();
  }, []);

  // Fetch sections when division changes
  useEffect(() => {
    if (mealBooking.divisionId && mealBooking.divisionId !== 'all') {
      fetchSectionsByDivision(mealBooking.divisionId);
    } else {
      setSections(allSections);
      setMealBooking(prev => ({ ...prev, sectionId: '' }));
    }
  }, [mealBooking.divisionId, allSections]);

  const fetchDivisions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/mysql/divisions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setDivisions(data.data || []);
      }
    } catch (err) {
      console.error('Error fetching divisions:', err);
    }
  };

  const fetchAllSections = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/mysql/sections', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setAllSections(data.data || []);
        setSections(data.data || []);
      }
    } catch (err) {
      console.error('Error fetching sections:', err);
    }
  };

  const fetchSectionsByDivision = async (divisionId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/mysql/sections?division_id=${divisionId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setSections(data.data || []);
      }
    } catch (err) {
      console.error('Error fetching sections by division:', err);
    }
  };

  const fetchTodaysBookingsCount = async () => {
    try {
      const token = localStorage.getItem('token');
      const today = new Date().toISOString().split('T')[0];
      const requestData = {
        startDate: today,
        endDate: today,
        filters: {},
        groupBy: 'user'
      };

      const response = await fetch('http://localhost:5000/api/reports/meal', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();
      if (response.ok && data.success) {
        setTodaysBookings(data.data?.length || 0);
      }
    } catch (error) {
      console.error('Error fetching today\'s bookings:', error);
    }
  };

  const handleBookingSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const token = localStorage.getItem('token');
      const mealData = {
        mealType: mealBooking.mealType,
        mealTime: new Date(`${mealBooking.mealDate}T12:00:00`),
        location: 'cafeteria',
        items: [{
          name: 'Standard Meal',
          category: 'main_course',
          quantity: parseInt(mealBooking.quantity),
          unit: 'serving',
          price: 0,
          calories: 0
        }],
        totalAmount: 0,
        paymentMethod: 'company_sponsored',
        paymentStatus: 'paid',
        specialRequirements: mealBooking.specialRequirements
      };

      const response = await fetch('http://localhost:5000/api/meals', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(mealData)
      });

      const result = await response.json();

      if (response.ok && result.success) {
        setMessage('Meal booking created successfully!');
        setMessageType('success');
        setMealBooking({
          divisionId: '',
          sectionId: '',
          mealDate: new Date().toISOString().split('T')[0],
          mealType: '',
          quantity: 1,
          specialRequirements: ''
        });
        // Refresh today's bookings count
        fetchTodaysBookingsCount();
        
        // Auto-dismiss success message after 3 seconds
        setTimeout(() => {
          setMessage('');
        }, 3000);
      } else {
        setMessage(result.message || 'Error creating meal booking');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Meal booking error:', error);
      setMessage('Network error occurred while creating meal booking');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const generateReport = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const requestData = {
        startDate: reportFilters.mealDate,
        endDate: reportFilters.mealDate,
        filters: {},
        groupBy: 'user'
      };

      const response = await fetch('http://localhost:5000/api/reports/meal', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setReportData(data.data || []);
      } else {
        setMessage(data.message || 'Error generating report');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Report generation error:', error);
      setMessage('Network error occurred while generating report');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const getMealTypeIcon = (mealType) => {
    const icons = {
      breakfast: 'ðŸŒ…',
      lunch: 'â˜€ï¸',
      dinner: 'ðŸŒ™',
      snack: 'ðŸª'
    };
    return icons[mealType] || 'ðŸ½ï¸';
  };

  const getMealTypeBadgeClass = (mealType) => {
    return `meal-badge meal-badge-${mealType}`;
  };

  const printReport = () => {
    window.print();
  };

  return (
    <div className="meal-management">
      {/* Header Section */}
      <div className="meal-header text-center">
        <h1 className="display-4 mb-3">ðŸ½ï¸ Meal Management System</h1>
        <p className="lead mb-2">Professional Meal Booking & Reporting for SLPA</p>
        <small className="text-light">Manage meal bookings, track consumption, and generate comprehensive reports</small>
      </div>

      {message && (
        <div className={`alert alert-${messageType === 'success' ? 'success' : 'danger'} alert-professional alert-dismissible fade show`}>
          <strong>{messageType === 'success' ? 'Success!' : 'Error!'}</strong> {message}
          <button type="button" className="btn-close" onClick={() => setMessage('')}></button>
        </div>
      )}

      <div className="meal-container">
        <div className="row g-4">
          {/* Meal Booking Form */}
          <div className="col-lg-4">
            <div className="meal-card">
              <div className="meal-card-header">
                <h4 className="mb-0">ðŸ“‹ Book New Meal</h4>
              </div>
              <div className="card-body p-4">
                <form onSubmit={handleBookingSubmit}>
                  <div className="meal-form-group">
                    <label className="meal-form-label">Division</label>
                    <select 
                      className="form-select meal-form-control"
                      value={mealBooking.divisionId}
                      onChange={(e) => setMealBooking(prev => ({ ...prev, divisionId: e.target.value }))}
                      required
                    >
                      <option value="">Select Division</option>
                      {divisions.map((division) => (
                        <option key={division.division_id} value={division.division_id}>
                          {division.division_name}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="meal-form-group">
                    <label className="meal-form-label">Section</label>
                    <select 
                      className="form-select meal-form-control"
                      value={mealBooking.sectionId}
                      onChange={(e) => setMealBooking(prev => ({ ...prev, sectionId: e.target.value }))}
                      required
                    >
                      <option value="">Select Section</option>
                      {sections.map((section) => (
                        <option key={section.section_id} value={section.section_id}>
                          {section.section_name}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="meal-form-group">
                    <label className="meal-form-label">Meal Date</label>
                    <input 
                      type="date"
                      className="form-control meal-form-control"
                      value={mealBooking.mealDate}
                      onChange={(e) => setMealBooking(prev => ({ ...prev, mealDate: e.target.value }))}
                      min={new Date().toISOString().split('T')[0]}
                      required
                    />
                  </div>

                  <div className="meal-form-group">
                    <label className="meal-form-label">Meal Type</label>
                    <select 
                      className="form-select meal-form-control"
                      value={mealBooking.mealType}
                      onChange={(e) => setMealBooking(prev => ({ ...prev, mealType: e.target.value }))}
                      required
                    >
                      <option value="">Select Meal Type</option>
                      <option value="breakfast">ðŸŒ… Breakfast</option>
                      <option value="lunch">â˜€ï¸ Lunch</option>
                      <option value="dinner">ðŸŒ™ Dinner</option>
                      <option value="snack">ðŸª Snack</option>
                    </select>
                  </div>

                  <div className="meal-form-group">
                    <label className="meal-form-label">Expected Quantity</label>
                    <input 
                      type="number"
                      className="form-control meal-form-control"
                      value={mealBooking.quantity}
                      onChange={(e) => setMealBooking(prev => ({ ...prev, quantity: e.target.value }))}
                      min="1"
                      max="500"
                      required
                    />
                  </div>

                  <div className="meal-form-group">
                    <label className="meal-form-label">Special Requirements</label>
                    <textarea 
                      className="form-control meal-form-control"
                      rows="3"
                      value={mealBooking.specialRequirements}
                      onChange={(e) => setMealBooking(prev => ({ ...prev, specialRequirements: e.target.value }))}
                      placeholder="Add Your Choice ..."
                    />
                  </div>
                  
                  <button type="submit" className="btn meal-btn meal-btn-primary w-100" disabled={loading}>
                    {loading ? (
                      <>
                        <i className="bi bi-arrow-clockwise spin"></i> Booking...
                      </>
                    ) : (
                      <>
                        <i className="bi bi-utensils"></i> Book Meal
                      </>
                    )}
                  </button>
                </form>
              </div>
            </div>

            {/* Quick Stats */}
            <div className="meal-stats-card">
              <div className="meal-stats-number">
                {reportData.length}
              </div>
              <div className="meal-stats-label">Today's Bookings</div>
            </div>
          </div>

          {/* Report Section */}
          <div className="col-lg-8">
            <div className="meal-card">
              <div className="meal-card-header">
                <h4 className="mb-0">ðŸ“Š Meal Reports & Analytics</h4>
              </div>
              <div className="card-body p-4">
                {/* Report Generation Form */}
                <form className="mb-4" onSubmit={(e) => { e.preventDefault(); generateReport(); }}>
                  <div className="row">
                    <div className="col-md-4">
                      <label className="meal-form-label">Division</label>
                      <select 
                        className="form-select meal-form-control"
                        value={reportFilters.divisionId}
                        onChange={(e) => setReportFilters(prev => ({ ...prev, divisionId: e.target.value }))}
                      >
                        <option value="">All Divisions</option>
                        {divisions.map((division) => (
                          <option key={division.division_id} value={division.division_id}>
                            {division.division_name}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="col-md-4">
                      <label className="meal-form-label">Report Date</label>
                      <input 
                        type="date"
                        className="form-control meal-form-control"
                        value={reportFilters.mealDate}
                        onChange={(e) => setReportFilters(prev => ({ ...prev, mealDate: e.target.value }))}
                        required
                      />
                    </div>
                    <div className="col-md-4">
                      <label className="meal-form-label" style={{ opacity: 0 }}>Generate</label>
                      <button type="submit" className="btn meal-btn meal-btn-success w-100" disabled={loading}>
                        {loading ? (
                          <>
                            <i className="bi bi-arrow-clockwise spin"></i> Generating...
                          </>
                        ) : (
                          <>
                            <i className="bi bi-chart-bar"></i> Generate Report
                          </>
                        )}
                      </button>
                    </div>
                  </div>
                </form>

                {/* Report Results */}
                {reportData.length > 0 ? (
                  <>
                    <div className="table-responsive">
                      <table className="table meal-table">
                        <thead>
                          <tr>
                            <th>Employee</th>
                            <th>Meal Type</th>
                            <th>Location</th>
                            <th>Time</th>
                            <th>Items</th>
                            <th>Quantity</th>
                            <th>Status</th>
                          </tr>
                        </thead>
                        <tbody>
                          {reportData.map((meal, index) => (
                            <tr key={index}>
                              <td>
                                <strong>
                                  {meal.user?.firstName && meal.user?.lastName 
                                    ? `${meal.user.firstName} ${meal.user.lastName}`
                                    : 'Unknown'}
                                </strong>
                                <br />
                                <small className="text-muted">
                                  {meal.user?.employeeId || 'N/A'}
                                </small>
                              </td>
                              <td>
                                <span className={getMealTypeBadgeClass(meal.mealType)}>
                                  {getMealTypeIcon(meal.mealType)} {meal.mealType}
                                </span>
                              </td>
                              <td style={{ textTransform: 'capitalize' }}>{meal.location}</td>
                              <td>
                                {new Date(meal.mealTime).toLocaleTimeString('en-GB', { 
                                  hour: '2-digit', minute: '2-digit' 
                                })}
                              </td>
                              <td>
                                <small>
                                  {meal.items?.map(item => `${item.name} (${item.quantity})`).join(', ') || 'Standard Meal'}
                                </small>
                              </td>
                              <td>
                                <span className="badge bg-primary rounded-pill">
                                  {meal.items?.reduce((total, item) => total + item.quantity, 0) || 1}
                                </span>
                              </td>
                              <td>
                                <span className={`meal-badge meal-badge-${meal.paymentStatus}`}>
                                  {meal.paymentStatus}
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>

                    {/* Summary Statistics */}
                    <div className="row mt-4">
                      <div className="col-md-3">
                        <div className="meal-stats-card" style={{ background: 'linear-gradient(135deg, #00b894 0%, #00a085 100%)' }}>
                          <div className="meal-stats-number">{reportData.length}</div>
                          <div className="meal-stats-label">Total Meals</div>
                        </div>
                      </div>
                      <div className="col-md-3">
                        <div className="meal-stats-card" style={{ background: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)' }}>
                          <div className="meal-stats-number">
                            {reportData.reduce((total, meal) => 
                              total + (meal.items?.reduce((sum, item) => sum + item.quantity, 0) || 1), 0)}
                          </div>
                          <div className="meal-stats-label">Total Quantity</div>
                        </div>
                      </div>
                      <div className="col-md-3">
                        <div className="meal-stats-card" style={{ background: 'linear-gradient(135deg, #fdcb6e 0%, #e17055 100%)' }}>
                          <div className="meal-stats-number">
                            {new Set(reportData.map(meal => meal.user?.employeeId)).size}
                          </div>
                          <div className="meal-stats-label">Unique Employees</div>
                        </div>
                      </div>
                      <div className="col-md-3">
                        <div className="meal-stats-card" style={{ background: 'linear-gradient(135deg, #a29bfe 0%, #6c5ce7 100%)' }}>
                          <div className="meal-stats-number">
                            {new Set(reportData.map(meal => meal.mealType)).size}
                          </div>
                          <div className="meal-stats-label">Meal Types</div>
                        </div>
                      </div>
                    </div>

                    <div className="text-center mt-4">
                      <button onClick={printReport} className="btn meal-btn meal-btn-primary">
                        <i className="bi bi-printer"></i> Print Report
                      </button>
                    </div>
                  </>
                ) : (
                  <div className="text-center py-5">
                    <i className="bi bi-utensils" style={{ fontSize: '3rem', color: '#cbd5e1' }}></i>
                    <h5 className="text-muted mt-3">No meal bookings found</h5>
                    <p className="text-muted">Select a division and date to generate a report</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MealManagement;
