import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import logo from '../../assets/logo.jpg';

const Login = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [videoLoaded, setVideoLoaded] = useState(false);
  const { login } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      await login(userId, password);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="position-relative vh-100 d-flex align-items-center justify-content-center overflow-hidden">
      {/* Video Background */}
      <video 
        autoPlay 
        muted 
        loop 
        playsInline
        className="position-fixed w-100 h-100"
        style={{ 
          top: 0, 
          left: 0, 
          objectFit: 'cover', 
          zIndex: -2,
          minWidth: '100%',
          minHeight: '100%'
        }}
        onLoadedData={() => {
          console.log('Video loaded successfully');
          setVideoLoaded(true);
        }}
        onError={(e) => {
          console.log('Video failed to load');
          setVideoLoaded(false);
        }}
      >
        <source src="/assets/background-video.mp4" type="video/mp4" />
        <source src="/assets/background-video.webm" type="video/webm" />
        Your browser does not support the video tag.
      </video>

      {/* Overlay for better readability */}
      <div 
        className="position-absolute w-100 h-100" 
        style={{ 
          top: 0, 
          left: 0, 
          backgroundColor: 'rgba(0, 0, 0, 0.2)', 
          backdropFilter: 'blur(1px)',
          zIndex: -1 
        }}
      ></div>

      {/* Login Container */}
      <div className="container-fluid">
        <div className="row justify-content-center align-items-center min-vh-100">
          <div className="col-11 col-sm-10 col-md-8 col-lg-6 col-xl-5 col-xxl-4">
            <div 
              className="card border-0" 
              style={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.15)', 
                backdropFilter: 'blur(20px)',
                borderRadius: '25px',
                padding: '3rem 2.5rem',
                boxShadow: '0 15px 35px rgba(0, 0, 0, 0.1), 0 5px 15px rgba(0, 0, 0, 0.07)',
                border: '1px solid rgba(255, 255, 255, 0.2)'
              }}
            >
              <div className="card-body p-0">
                <div className="row align-items-center">
                  {/* Left Side - Logo and Title */}
                  <div className="col-12 col-lg-5 text-center mb-4 mb-lg-0">
                    <div className="mb-4">
                      <img 
                        src={logo} 
                        alt="Logo" 
                        className="rounded-circle"
                        style={{ 
                          width: '140px', 
                          height: '140px', 
                          objectFit: 'cover',
                          border: '4px solid rgba(255, 255, 255, 0.3)',
                          boxShadow: '0 10px 30px rgba(0, 0, 0, 0.2)'
                        }}
                      />
                    </div>
                    <h1 className="fw-bold text-dark mb-3" 
                        style={{ 
                          fontSize: '2.8rem', 
                          letterSpacing: '0.2rem',
                          fontFamily: 'Arial, sans-serif',
                          textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
                        }}>
                      LOGIN
                    </h1>
                    <div 
                      className="mx-auto" 
                      style={{ 
                        height: '4px', 
                        width: '80px', 
                        backgroundColor: 'rgba(108, 117, 125, 0.8)', 
                        borderRadius: '2px'
                      }}
                    ></div>
                  </div>

                  {/* Right Side - Form */}
                  <div className="col-12 col-lg-7">
                    <form onSubmit={handleSubmit}>
                      {error && (
                        <div className="alert alert-danger text-center mb-3" role="alert">
                          {error}
                        </div>
                      )}

                      {/* User ID Field */}
                      <div className="mb-4">
                        <label htmlFor="userId" className="form-label text-light fw-semibold mb-2 d-flex align-items-center">
                          <i className="bi bi-person-fill me-2" style={{ fontSize: '1.2rem' }}></i>
                          USER ID
                        </label>
                        <input
                          id="userId"
                          type="text"
                          className="form-control form-control-lg"
                          placeholder="SA1001"
                          value={userId}
                          onChange={(e) => setUserId(e.target.value)}
                          required
                          autoFocus
                          style={{ 
                            backgroundColor: 'rgba(255, 255, 255, 0.9)', 
                            border: 'none',
                            borderRadius: '15px',
                            padding: '18px 25px',
                            fontSize: '16px',
                            fontWeight: '500',
                            boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                            backdropFilter: 'blur(10px)'
                          }}
                        />
                      </div>

                      {/* Password Field */}
                      <div className="mb-4">
                        <label htmlFor="password" className="form-label text-light fw-semibold mb-2 d-flex align-items-center">
                          <i className="bi bi-lock-fill me-2" style={{ fontSize: '1.2rem' }}></i>
                          PASSWORD
                        </label>
                        <input
                          id="password"
                          type="password"
                          className="form-control form-control-lg"
                          placeholder="••••••••••••"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          required
                          style={{ 
                            backgroundColor: 'rgba(255, 255, 255, 0.9)', 
                            border: 'none',
                            borderRadius: '15px',
                            padding: '18px 25px',
                            fontSize: '16px',
                            fontWeight: '500',
                            boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
                            backdropFilter: 'blur(10px)'
                          }}
                        />
                      </div>

                      {/* Forgot Password */}
                      <div className="text-end mb-4">
                        <button
                          type="button"
                          className="btn btn-link text-light p-0"
                          onClick={() => navigate('/forgot-password')}
                          style={{ 
                            textDecoration: 'none', 
                            fontSize: '14px',
                            fontWeight: '500',
                            opacity: '0.9'
                          }}
                          onMouseEnter={(e) => {
                            e.target.style.opacity = '1';
                            e.target.style.textDecoration = 'underline';
                          }}
                          onMouseLeave={(e) => {
                            e.target.style.opacity = '0.9';
                            e.target.style.textDecoration = 'none';
                          }}
                        >
                          Forgot Password?
                        </button>
                      </div>

                      {/* Login Button */}
                      <button
                        type="submit"
                        className="btn btn-lg w-100 fw-bold text-white"
                        disabled={loading}
                        style={{
                          background: 'linear-gradient(135deg, #4285f4, #1a73e8)',
                          border: 'none',
                          borderRadius: '15px',
                          padding: '18px',
                          fontSize: '18px',
                          letterSpacing: '0.1rem',
                          fontWeight: '700',
                          textTransform: 'uppercase',
                          transition: 'all 0.3s ease',
                          boxShadow: '0 8px 25px rgba(66, 133, 244, 0.4)'
                        }}
                        onMouseEnter={(e) => {
                          if (!loading) {
                            e.target.style.transform = 'translateY(-2px)';
                            e.target.style.boxShadow = '0 12px 35px rgba(66, 133, 244, 0.5)';
                          }
                        }}
                        onMouseLeave={(e) => {
                          e.target.style.transform = 'translateY(0)';
                          e.target.style.boxShadow = '0 8px 25px rgba(66, 133, 244, 0.4)';
                        }}
                      >
                        {loading ? (
                          <>
                            <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                            LOGGING IN...
                          </>
                        ) : (
                          'LOGIN'
                        )}
                      </button>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
