import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import Sidebar from './Sidebar';
import Header from './Header';
import DashboardStats from './DashboardStats';
import './Dashboard.css';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const { user } = useContext(AuthContext);

  const renderActiveSection = () => {
    switch (activeSection) {
      case 'dashboard':
        return <DashboardStats />;
      case 'users':
        return <div className="coming-soon">User Management - Coming Soon</div>;
      case 'reports':
      case 'unit-attendance':
      case 'audit-report':
      case 'meal-report':
        return <div className="coming-soon">Report Generation - Coming Soon</div>;
      case 'divisions':
        return <div className="coming-soon">Division Management - Coming Soon</div>;
      case 'sections':
        return <div className="coming-soon">Section Management - Coming Soon</div>;
      case 'roles':
        return <div className="coming-soon">Role Access Management - Coming Soon</div>;
      case 'settings':
        return <div className="coming-soon">Settings - Coming Soon</div>;
      default:
        return <DashboardStats />;
    }
  };

  return (
    <div className="dashboard-container">
      <Sidebar 
        activeSection={activeSection}
        setActiveSection={setActiveSection}
        collapsed={sidebarCollapsed}
        setCollapsed={setSidebarCollapsed}
        userRole={user?.role}
      />
      <div className={`main-content ${sidebarCollapsed ? 'collapsed' : ''}`}>
        <Header 
          toggleSidebar={() => setSidebarCollapsed(!sidebarCollapsed)}
          user={user}
        />
        <div className="content-area">
          {renderActiveSection()}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
