import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import logo from '../../assets/logo.jpg';

const Login = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext) || {};
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      await login(userId, password);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="position-relative vh-100 d-flex align-items-center justify-content-center overflow-hidden">
      {/* Video Background */}
      <video 
        autoPlay 
        muted 
        loop 
        className="position-absolute w-100 h-100"
        style={{ 
          top: 0, 
          left: 0, 
          objectFit: 'cover', 
          zIndex: -2 
        }}
      >
        <source src="/assets/web_.mp4" type="video/mp4" />
        <source src="/assets/background-video.webm" type="video/webm" />
        Your browser does not support the video tag.
      </video>

      {/* Dark Overlay */}
      <div 
        className="position-absolute w-100 h-100" 
        style={{ 
          top: 0, 
          left: 0, 
          backgroundColor: 'rgba(59, 130, 246, 0.6)', 
          backdropFilter: 'blur(2px)',
          zIndex: -1 
        }}
      ></div>

      {/* Login Container */}
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-12 col-md-10 col-lg-8 col-xl-6">
            <div 
              className="card shadow-lg border-0" 
              style={{ 
                backgroundColor: 'rgba(255, 255, 255, 0.2)', 
                backdropFilter: 'blur(10px)',
                borderRadius: '2rem'
              }}
            >
              <div className="card-body p-4 p-md-5">
                <div className="row align-items-center">
                  {/* Logo Section */}
                  <div className="col-12 col-md-6 text-center mb-4 mb-md-0">
                    <img 
                      src={logo} 
                      alt="Logo" 
                      className="img-fluid rounded-circle shadow mb-3"
                      style={{ width: '150px', height: '150px', objectFit: 'cover' }}
                    />
                    <h1 className="fw-bold text-dark mb-2" style={{ fontSize: '2.5rem', letterSpacing: '0.2rem' }}>
                      LOGIN
                    </h1>
                    <div 
                      className="mx-auto mb-2" 
                      style={{ 
                        height: '4px', 
                        width: '60px', 
                        backgroundColor: '#6c757d', 
                        borderRadius: '2px' 
                      }}
                    ></div>
                  </div>

                  {/* Form Section */}
                  <div className="col-12 col-md-6">
                    <form onSubmit={handleSubmit}>
                      {error && (
                        <div className="alert alert-danger text-center" role="alert">
                          {error}
                        </div>
                      )}

                      <div className="mb-3">
                        <label htmlFor="userId" className="form-label text-light fw-semibold">
                          <i className="bi bi-person-fill me-2"></i>USER ID
                        </label>
                        <input
                          id="userId"
                          type="text"
                          className="form-control"
                          placeholder="SA1001"
                          value={userId}
                          onChange={(e) => setUserId(e.target.value)}
                          required
                          autoFocus
                          style={{ 
                            backgroundColor: 'rgba(255, 255, 255, 0.8)', 
                            border: 'none',
                            borderRadius: '0.5rem',
                            padding: '0.75rem 1rem'
                          }}
                        />
                      </div>

                      <div className="mb-3">
                        <label htmlFor="password" className="form-label text-light fw-semibold">
                          <i className="bi bi-lock-fill me-2"></i>PASSWORD
                        </label>
                        <input
                          id="password"
                          type="password"
                          className="form-control"
                          placeholder="••••••••••••"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          required
                          style={{ 
                            backgroundColor: 'rgba(255, 255, 255, 0.8)', 
                            border: 'none',
                            borderRadius: '0.5rem',
                            padding: '0.75rem 1rem'
                          }}
                        />
                      </div>

                      <div className="text-end mb-3">
                        <button
                          type="button"
                          className="btn btn-link text-light p-0"
                          onClick={() => navigate('/forgot-password')}
                          style={{ textDecoration: 'none', fontSize: '0.9rem' }}
                        >
                          Forgot Password?
                        </button>
                      </div>

                      <button
                        type="submit"
                        className="btn w-100 fw-bold text-white"
                        disabled={loading}
                        style={{
                          background: 'linear-gradient(135deg, #3b82f6, #1d4ed8)',
                          border: 'none',
                          borderRadius: '0.5rem',
                          padding: '0.75rem',
                          fontSize: '1.1rem',
                          letterSpacing: '0.1rem'
                        }}
                      >
                        {loading ? 'Logging in...' : 'LOGIN'}
                      </button>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
