import React, { useState, useEffect } from 'react';

const SectionManagement = () => {
  const [sections, setSections] = useState([]);
  const [divisions, setDivisions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [currentSection, setCurrentSection] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    division: '',
    description: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);

  // Fetch sections and divisions
  const fetchData = async () => {
    try {
      // Mock sections data
      setSections([
        { 
          id: 1, 
          name: 'Software Development', 
          division: '689443a4c8066bb7f0b32a44', 
          employeeCount: 45,
          description: 'Development and maintenance of software systems',
          isActive: true,
          createdAt: '2024-01-15'
        },
        { 
          id: 2, 
          name: 'Quality Assurance', 
          division: '689443a4c8066bb7f0b32a44', 
          employeeCount: 25,
          description: 'Testing and quality control',
          isActive: true,
          createdAt: '2024-02-10'
        },
        { 
          id: 3, 
          name: 'Recruitment', 
          division: '5', 
          employeeCount: 12,
          description: 'Talent acquisition and hiring',
          isActive: true,
          createdAt: '2024-03-05'
        }
      ]);

      // Mock divisions data (should match DivisionManagement)
      setDivisions([
        { _id: '689443a4c8066bb7f0b32a44', name: 'Information Technology', code: 'IT' },
        { _id: '1', name: 'Administration', code: 'ADM' },
        { _id: '2', name: 'Operations', code: 'OPS' },
        { _id: '3', name: 'Finance', code: 'FIN' },
        { _id: '4', name: 'Engineering', code: 'ENG' },
        { _id: '5', name: 'Human Resources', code: 'HR' },
        { _id: '6', name: 'Security', code: 'SEC' },
        { _id: '7', name: 'Marine Services', code: 'MAR' }
      ]);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  // Helper function to get division name by ID
  const getDivisionName = (divisionId) => {
    const division = divisions.find(div => (div._id || div.id) === divisionId);
    return division ? division.name : 'N/A';
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error for this field
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Validate form
  const validateForm = () => {
    const errors = {};
    if (!formData.name.trim()) {
      errors.name = 'Section name is required';
    }
    
    if (!formData.division) {
      errors.division = 'Division is required';
    }
    
    return errors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    setSubmitting(true);
    try {
      // Prepare the data
      const submitData = {
        name: formData.name.trim(),
        division: formData.division,
        description: formData.description.trim()
      };

      console.log('Submitting section data:', submitData);
      
      if (currentSection) {
        // Update existing section
        setSections(sections.map(section => 
          section.id === currentSection.id 
            ? { 
                ...section, 
                ...submitData, 
                updatedAt: new Date().toISOString().split('T')[0] 
              }
            : section
        ));
        alert('Section updated successfully!');
      } else {
        // Add new section
        const newSection = {
          ...submitData,
          id: Date.now(), // Temporary ID
          employeeCount: 0,
          isActive: true,
          createdAt: new Date().toISOString().split('T')[0]
        };
        setSections([...sections, newSection]);
        alert('Section created successfully!');
      }
      
      handleCloseModal();
    } catch (error) {
      console.error('Error submitting section:', error);
      alert(`Error ${currentSection ? 'updating' : 'adding'} section. Please try again.`);
    } finally {
      setSubmitting(false);
    }
  };

  // Handle opening edit modal
  const handleEdit = (section) => {
    console.log('Editing section:', section);
    setCurrentSection(section);
    setFormData({
      name: section.name || '',
      division: section.division || '',
      description: section.description || ''
    });
    setFormErrors({});
    setShowEditModal(true);
  };

  // Handle delete section
  const handleDelete = async (section) => {
    if (window.confirm(`Are you sure you want to delete "${section.name}" section? This action cannot be undone.`)) {
      try {
        setSections(sections.filter(s => s.id !== section.id));
        alert('Section deleted successfully!');
      } catch (error) {
        console.error('Error deleting section:', error);
        alert('Error deleting section. Please try again.');
      }
    }
  };

  // Handle opening add modal
  const handleAdd = () => {
    setCurrentSection(null);
    setFormData({ name: '', division: '', description: '' });
    setFormErrors({});
    setShowAddModal(true);
  };

  // Handle closing modals
  const handleCloseModal = () => {
    setShowAddModal(false);
    setShowEditModal(false);
    setCurrentSection(null);
    setFormData({ name: '', division: '', description: '' });
    setFormErrors({});
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) {
    return <div className="loading-container">Loading...</div>;
  }

  return (
    <div className="section-management">
      <div className="page-header">
        <h2><i className="bi bi-diagram-3"></i> Section Management</h2>
        <button className="btn btn-primary">
          <i className="bi bi-plus-circle"></i> Add Section
        </button>
      </div>

      <div className="sections-table-container">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Section Name</th>
              <th>Division</th>
              <th>Employee Count</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {sections.map(section => (
              <tr key={section.id}>
                <td>{section.name}</td>
                <td>{section.division}</td>
                <td>{section.employeeCount}</td>
                <td>
                  <button className="btn btn-sm btn-outline-primary me-2">
                    <i className="bi bi-pencil"></i>
                  </button>
                  <button className="btn btn-sm btn-outline-danger">
                    <i className="bi bi-trash"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default SectionManagement;
