import React, { useState, useEffect } from 'react';
import './RoleAccessManagement.css';

const RoleAccessManagement = () => {
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedRole, setSelectedRole] = useState('');
  const [selectAll, setSelectAll] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    displayName: '',
    description: '',
    permissions: []
  });
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  // Available permissions list
  const availablePermissions = [
    { id: 'all_access', name: 'All Access', description: 'Full system access and administration' },
    { id: 'user_management', name: 'User Management', description: 'Create, edit, delete users' },
    { id: 'role_management', name: 'Role Management', description: 'Manage roles and permissions' },
    { id: 'division_management', name: 'Division Management', description: 'Manage company divisions' },
    { id: 'section_management', name: 'Section Management', description: 'Manage department sections' },
    { id: 'attendance_management', name: 'Attendance Management', description: 'Manage attendance records' },
    { id: 'attendance_view', name: 'View Attendance', description: 'View attendance reports' },
    { id: 'reports_view', name: 'View Reports', description: 'Access system reports' },
    { id: 'reports_generate', name: 'Generate Reports', description: 'Generate and export reports' },
    { id: 'system_settings', name: 'System Settings', description: 'Configure system settings' },
    { id: 'audit_logs', name: 'Audit Logs', description: 'View system audit logs' },
    { id: 'profile_edit', name: 'Edit Profile', description: 'Edit personal profile' },
    { id: 'unit_reports', name: 'Unit Reports', description: 'Unit attendance reports' },
    { id: 'meal_reports', name: 'Meal Reports', description: 'Meal tracking reports' },
    { id: 'employee_records', name: 'Employee Records', description: 'Manage employee records' },
    { id: 'change_password', name: 'Change Password', description: 'Change user passwords' }
  ];

  // Mock roles data
  const mockRoles = [
    {
      _id: '1',
      name: 'super_admin',
      displayName: 'Super Administrator',
      description: 'Full system access with all permissions',
      permissions: ['all_access', 'user_management', 'role_management', 'system_settings', 'audit_logs'],
      isSystem: true,
      isActive: true,
      createdAt: '2024-01-01'
    },
    {
      _id: '2',
      name: 'administrator',
      displayName: 'Administrator',
      description: 'Administrative access with user and content management',
      permissions: ['user_management', 'division_management', 'section_management', 'attendance_management', 'reports_generate'],
      isSystem: true,
      isActive: true,
      createdAt: '2024-01-01'
    },
    {
      _id: '3',
      name: 'manager',
      displayName: 'Manager',
      description: 'Management level access with reporting capabilities',
      permissions: ['attendance_view', 'reports_view', 'reports_generate', 'section_management'],
      isSystem: false,
      isActive: true,
      createdAt: '2024-02-01'
    },
    {
      _id: '4',
      name: 'employee',
      displayName: 'Employee',
      description: 'Basic employee access',
      permissions: ['attendance_view', 'profile_edit'],
      isSystem: true,
      isActive: true,
      createdAt: '2024-01-01'
    }
  ];

  // Fetch roles data
  const fetchData = async () => {
    try {
      setRoles(mockRoles);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching roles:', error);
      setLoading(false);
    }
  };

  // Handle role selection
  const handleRoleSelect = (e) => {
    const roleValue = e.target.value;
    setSelectedRole(roleValue);
    
    if (roleValue) {
      const role = roles.find(r => r.name === roleValue);
      if (role) {
        setFormData(prev => ({
          ...prev,
          permissions: role.permissions || []
        }));
        updateSelectAllStatus(role.permissions || []);
      }
    } else {
      setFormData(prev => ({
        ...prev,
        permissions: []
      }));
      setSelectAll(false);
    }
  };

  // Handle permission checkbox changes
  const handlePermissionChange = (permissionId) => {
    const newPermissions = formData.permissions.includes(permissionId)
      ? formData.permissions.filter(p => p !== permissionId)
      : [...formData.permissions, permissionId];
    
    setFormData(prev => ({
      ...prev,
      permissions: newPermissions
    }));
    
    updateSelectAllStatus(newPermissions);
  };

  // Handle select all toggle
  const handleSelectAll = () => {
    const newSelectAll = !selectAll;
    setSelectAll(newSelectAll);
    
    if (newSelectAll) {
      const allPermissionIds = availablePermissions.map(p => p.id);
      setFormData(prev => ({
        ...prev,
        permissions: allPermissionIds
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        permissions: []
      }));
    }
  };

  // Update select all status
  const updateSelectAllStatus = (permissions) => {
    const allPermissionIds = availablePermissions.map(p => p.id);
    setSelectAll(permissions.length === allPermissionIds.length && allPermissionIds.length > 0);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedRole) {
      setMessage('Please select a role first');
      setMessageType('error');
      return;
    }

    setSubmitting(true);
    
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Update the role in state
      setRoles(prevRoles => 
        prevRoles.map(role => 
          role.name === selectedRole 
            ? { ...role, permissions: formData.permissions, updatedAt: new Date().toISOString() }
            : role
        )
      );
      
      setMessage('Access control updated successfully!');
      setMessageType('success');
      
      // Auto-hide message after 5 seconds
      setTimeout(() => {
        setMessage('');
        setMessageType('');
      }, 5000);
      
    } catch (error) {
      console.error('Error updating permissions:', error);
      setMessage('Error updating access control. Please try again.');
      setMessageType('error');
    } finally {
      setSubmitting(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="role-access-wrapper">
        <div className="loading-overlay">
          <div className="loading-spinner"></div>
        </div>
      </div>
    );
  }

  const selectedRoleData = roles.find(r => r.name === selectedRole);
  const currentPermissions = selectedRoleData ? selectedRoleData.permissions : [];

  return (
    <div className="role-access-wrapper">
      {/* Decorative Elements */}
      <div className="decorative-elements">
        <div className="geometric-shape shape-1"></div>
        <div className="geometric-shape shape-2"></div>
        <div className="geometric-shape shape-3"></div>
        <div className="geometric-shape shape-4"></div>
      </div>

      <div className="container-fluid px-4">
        <div className="main-card">
          <div className="card-header-custom">
            <div className="icon-wrapper-large">
              <i className="bi bi-shield-check"></i>
            </div>
            <h1 className="page-title">Role Access Management</h1>
            <p className="page-subtitle">Configure user role permissions and access control</p>
          </div>
          
          <div className="card-body-custom">
            {/* Success/Error Messages */}
            {message && (
              <div className={`alert ${messageType === 'success' ? 'alert-success-modern' : 'alert-error-modern'}`}>
                <i className={`bi ${messageType === 'success' ? 'bi-check-circle' : 'bi-exclamation-circle'} mr-2`}></i>
                {message}
              </div>
            )}

            {/* Stats Section */}
            <div className="stats-section">
              <div className="stats-item">
                <span className="stats-number">{roles.length}</span>
                <span className="stats-label">Total Roles</span>
              </div>
              <div className="stats-item">
                <span className="stats-number">{availablePermissions.length}</span>
                <span className="stats-label">Available Permissions</span>
              </div>
              {selectedRole && (
                <div className="stats-item">
                  <span className="stats-number">{currentPermissions.length}</span>
                  <span className="stats-label">Granted Permissions</span>
                </div>
              )}
            </div>

            {/* Role Selection */}
            <div className="form-section">
              <label htmlFor="role" className="form-label-modern">
                <i className="bi bi-person-gear text-primary mr-2"></i>Select Role to Manage
              </label>
              <select 
                name="role" 
                id="role" 
                className="form-control form-control-modern" 
                value={selectedRole}
                onChange={handleRoleSelect}
              >
                <option value="">-- Choose a role to configure --</option>
                {roles.map(role => (
                  <option key={role._id} value={role.name}>
                    {role.displayName} ({role.name})
                  </option>
                ))}
              </select>
            </div>

            {/* Permissions Configuration */}
            {selectedRole && (
              <div className="form-section">
                <form onSubmit={handleSubmit}>
                  <h3 style={{ color: '#333', marginBottom: '20px', fontWeight: '600' }}>
                    <i className="bi bi-shield-alt text-primary mr-2"></i>
                    Configure Access Permissions for: <span style={{ color: '#667eea' }}>{selectedRoleData?.displayName}</span>
                  </h3>

                  {/* Select All Section */}
                  <div className="select-all-section">
                    <input 
                      type="checkbox" 
                      id="select-all"
                      checked={selectAll}
                      onChange={handleSelectAll}
                    />
                    <label htmlFor="select-all">
                      <i className="bi bi-check2-all mr-2"></i>Select All Permissions
                    </label>
                    <span className="permission-counter">
                      {formData.permissions.length}/{availablePermissions.length} permissions selected
                    </span>
                  </div>

                  {/* Permissions Grid */}
                  <div className="checkbox-grid">
                    {availablePermissions.map(permission => (
                      <div 
                        key={permission.id} 
                        className={`checkbox-item ${formData.permissions.includes(permission.id) ? 'selected' : ''}`}
                        onClick={() => handlePermissionChange(permission.id)}
                      >
                        <input 
                          type="checkbox" 
                          checked={formData.permissions.includes(permission.id)}
                          onChange={() => handlePermissionChange(permission.id)}
                          id={`perm_${permission.id}`}
                        />
                        <span className="checkmark"></span>
                        <label htmlFor={`perm_${permission.id}`}>
                          <div className="permission-title">
                            <i className="bi bi-file-text text-muted mr-2"></i>
                            {permission.name}
                          </div>
                          <div className="permission-description">
                            {permission.description}
                          </div>
                        </label>
                      </div>
                    ))}
                  </div>

                  {/* Submit Button */}
                  <div className="text-center mt-4">
                    <button 
                      type="submit" 
                      className="btn-save-modern"
                      disabled={submitting}
                    >
                      {submitting ? (
                        <>
                          <div className="spinner-border spinner-border-sm mr-2" role="status">
                            <span className="visually-hidden">Loading...</span>
                          </div>
                          Saving...
                        </>
                      ) : (
                        <>
                          <i className="bi bi-save mr-2"></i>
                          Save Access Configuration
                        </>
                      )}
                    </button>
                  </div>
                </form>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Loading Overlay */}
      {submitting && (
        <div className="loading-overlay">
          <div className="loading-spinner"></div>
        </div>
      )}
    </div>
  );
};

export default RoleAccessManagement;
