import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import './RoleAccessManagement.css';

const structuredPermissions = [
  {
    category: 'users',
    name: 'Users',
    permissions: [
      { id: 'create', name: 'Create Users', description: 'Can create new users' },
      { id: 'read', name: 'Read Users', description: 'Can view user details' },
      { id: 'update', name: 'Update Users', description: 'Can update user info' },
      { id: 'delete', name: 'Delete Users', description: 'Can delete users' },
    ]
  },
  {
    category: 'attendance',
    name: 'Attendance',
    permissions: [
      { id: 'create', name: 'Create Attendance', description: 'Can create attendance records' },
      { id: 'read', name: 'Read Attendance', description: 'Can view attendance data' },
      { id: 'update', name: 'Update Attendance', description: 'Can modify attendance records' },
      { id: 'delete', name: 'Delete Attendance', description: 'Can delete attendance data' },
    ]
  },
  {
    category: 'reports',
    name: 'Reports',
    permissions: [
      { id: 'create', name: 'Create Reports', description: 'Can create reports' },
      { id: 'read', name: 'Read Reports', description: 'Can view reports' },
      { id: 'update', name: 'Update Reports', description: 'Can modify reports' },
      { id: 'delete', name: 'Delete Reports', description: 'Can delete reports' },
    ]
  },
  {
    category: 'roles',
    name: 'Roles',
    permissions: [
      { id: 'create', name: 'Create Roles', description: 'Can create new roles' },
      { id: 'read', name: 'Read Roles', description: 'Can view roles' },
      { id: 'update', name: 'Update Roles', description: 'Can modify roles' },
      { id: 'delete', name: 'Delete Roles', description: 'Can delete roles' },
    ]
  }
];

const defaultPermissions = {
  users: { create: false, read: false, update: false, delete: false },
  attendance: { create: false, read: false, update: false, delete: false },
  reports: { create: false, read: false, update: false, delete: false },
  roles: { create: false, read: false, update: false, delete: false },
};

const availableRoles = [
  { value: 'super_admin', label: 'Super Admin', description: 'Highest level system administrator' },
  { value: 'admin', label: 'Administrator', description: 'System administrator with management rights' },
  { value: 'clerk', label: 'Clerk', description: 'General office clerk' },
];

const RoleAccessManagement = () => {
  const { user } = useContext(AuthContext);
  const isSuperAdmin = user?.role === 'super_admin';

  // Permissions for selected role
  const [selectedRole, setSelectedRole] = useState('');
  const [formData, setFormData] = useState({ permissions: { ...defaultPermissions } });
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);

  // Permission checks
  const hasRoleReadPermission = () => isSuperAdmin || user?.permissions?.roles?.read;
  const hasRoleUpdatePermission = () => isSuperAdmin || user?.permissions?.roles?.update;

  // Stats
  const getTotalAvailablePermissions = () =>
    structuredPermissions.reduce((sum, cat) => sum + cat.permissions.length, 0);
  const getTotalEnabledPermissions = () =>
    Object.values(formData.permissions)
      .flatMap(cat => Object.values(cat))
      .filter(Boolean).length;

  // Handlers
  const handleRoleSelect = (e) => {
    setSelectedRole(e.target.value);
    // Reset permissions for demo; in real app, fetch from backend
    setFormData({ permissions: { ...defaultPermissions } });
  };

  const handlePermissionChange = (category, id) => {
    setFormData(prev => ({
      permissions: {
        ...prev.permissions,
        [category]: {
          ...prev.permissions[category],
          [id]: !prev.permissions[category][id]
        }
      }
    }));
  };

  const handleSelectAll = () => {
    const allEnabled = getTotalEnabledPermissions() !== getTotalAvailablePermissions();
    const updated = {};
    structuredPermissions.forEach(cat => {
      updated[cat.category] = {};
      cat.permissions.forEach(p => {
        updated[cat.category][p.id] = allEnabled;
      });
    });
    setFormData({ permissions: updated });
  };

  const openConfirm = (e) => { e.preventDefault(); setShowConfirm(true); };
  const confirmAndSave = () => {
    setShowConfirm(false);
    setShowSuccessModal(true);
    // In real app, save to backend here
  };

  return (
    <div className="role-access-wrapper">
      <div className="section-header">
        <h2>
          <i className="bi bi-people-fill"></i>
          Role Access Management
        </h2>
        <div className="header-actions">
          <button className="btn-manage-roles">
            <i className="bi bi-gear" style={{ marginRight: '6px', fontSize: '1.1rem' }}></i> Manage Roles
          </button>
        </div>
      </div>
      <div className="container-fluid px-4">
        {hasRoleReadPermission() ? (
          <>
            <div className="stats-section">
              <div className="stats-item">
                <span className="stats-number">{availableRoles.length}</span>
                <span className="stats-label">Available Roles</span>
              </div>
              <div className="stats-item">
                <span className="stats-number">{getTotalAvailablePermissions()}</span>
                <span className="stats-label">Available Permissions</span>
              </div>
              {selectedRole && (
                <div className="stats-item">
                  <span className="stats-number">-</span>
                  <span className="stats-label">Users with this Role</span>
                </div>
              )}
              {selectedRole && (
                <div className="stats-item">
                  <span className="stats-number">{getTotalEnabledPermissions()}</span>
                  <span className="stats-label">Granted Permissions</span>
                </div>
              )}
            </div>

            <div className="form-section">
              <label htmlFor="role" className="form-label-modern">
                <i className="bi bi-person-gear text-primary mr-2"></i>Select Role to Manage
              </label>
              <select 
                name="role" 
                id="role" 
                className="form-control form-control-modern" 
                value={selectedRole}
                onChange={handleRoleSelect}
                disabled={!hasRoleUpdatePermission()}
                title={!hasRoleUpdatePermission() ? 'You need "Update Role Permissions" access to change role selection' : ''}
              >
                <option value="">-- Choose a role to configure --</option>
                {availableRoles.map(role => (
                  <option key={role.value} value={role.value}>
                    {role.label} - {role.description}
                  </option>
                ))}
              </select>
              {selectedRole && (
                <small className="text-muted mt-2 d-block">
                  <i className="bi bi-info-circle mr-1"></i>
                  This will update permissions for users with the role: {availableRoles.find(r => r.value === selectedRole)?.label || selectedRole}
                </small>
              )}
              {!hasRoleUpdatePermission() && (
                <div className="alert alert-warning mt-3">
                  <i className="bi bi-lock-fill mr-2"></i>
                  You have view-only access. To modify role permissions, you need the "Update Role Permissions" access.
                </div>
              )}
            </div>

            {/* Permissions Grid by Category */}
            {selectedRole && (
              <div className="form-section">
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '12px' }}>
                  <h4 style={{ margin: 0 }}>Permissions for <strong>{availableRoles.find(r => r.value === selectedRole)?.label || selectedRole}</strong>:</h4>
                  <button className="btn-save-modern" style={{ fontSize: '13px', padding: '8px 18px' }} onClick={handleSelectAll}>
                    {getTotalEnabledPermissions() === getTotalAvailablePermissions() ? 'Unselect All' : 'Select All'}
                  </button>
                </div>
                {structuredPermissions.map(category => (
                  <div key={category.category} className="permission-category">
                    <h5 style={{ marginBottom: '8px', color: '#2575fc' }}>{category.name}</h5>
                    <div className="checkbox-grid">
                      {category.permissions.map(permission => {
                        const isChecked = formData.permissions[category.category]?.[permission.id] || false;
                        return (
                          <label key={`${category.category}_${permission.id}`} style={{ display: 'inline-flex', alignItems: 'center', marginRight: '18px', marginBottom: '8px', fontSize: '15px' }}>
                            <input
                              type="checkbox"
                              checked={isChecked}
                              onChange={() => handlePermissionChange(category.category, permission.id)}
                              disabled={!hasRoleUpdatePermission()}
                              style={{ marginRight: '7px', accentColor: '#2575fc' }}
                            />
                            {permission.name}
                          </label>
                        );
                      })}
                    </div>
                  </div>
                ))}
                <div style={{ marginTop: '18px', textAlign: 'right' }}>
                  <button className="btn-save-modern" style={{ fontSize: '15px', padding: '10px 32px' }} onClick={openConfirm} disabled={!hasRoleUpdatePermission()}>
                    Save Changes
                  </button>
                </div>
              </div>
            )}
          </>
        ) : (
          <div className="alert alert-warning mt-4">
            <i className="bi bi-lock-fill mr-2"></i>
            You do not have permission to view role access management. Contact a Super Admin for "roles.read" access.
          </div>
        )}
      </div>

      {/* Confirm Modal */}
      {showConfirm && (
        <div className="modal-overlay" onClick={() => setShowConfirm(false)}>
          <div className="modal-content confirm-modal" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h4>Confirm Save</h4>
            </div>
            <div className="modal-body">
              <p>Are you sure you want to save these permission changes for <strong>{availableRoles.find(r => r.value === selectedRole)?.label || selectedRole}</strong>?</p>
            </div>
            <div className="modal-footer">
              <button className="btn-save-modern btn-secondary" onClick={() => setShowConfirm(false)}>
                Cancel
              </button>
              <button className="btn-save-modern btn-success" onClick={confirmAndSave}>
                Yes, Save
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Success Modal */}
      {showSuccessModal && (
        <div className="modal-overlay" style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)', zIndex: 9999 }}>
          <div className="modal-content success-modal-content" onClick={e => e.stopPropagation()} style={{ maxWidth: '400px', textAlign: 'center', border: '3px solid #2575fc', borderRadius: '15px', background: 'linear-gradient(135deg, #f8f9fa, #e9ecef)', boxShadow: '0 10px 30px rgba(37,117,252,0.15)', animation: 'fadeInUp 0.3s ease-out' }}>
            <div style={{ padding: '30px' }}>
              <div style={{ fontSize: '48px', color: '#2575fc', marginBottom: '18px', animation: 'bounce 1s ease-in-out' }}>
                <i className="bi bi-check-circle-fill"></i>
              </div>
              <h2 style={{ color: '#2575fc', marginBottom: '12px', fontWeight: 'bold' }}>
                Success!
              </h2>
              <p style={{ fontSize: '15px', color: '#495057', marginBottom: '18px', lineHeight: '1.5' }}>
                Permissions updated for <strong>{availableRoles.find(r => r.value === selectedRole)?.label || selectedRole}</strong>.
              </p>
              <button className="btn-save-modern btn-success" onClick={() => setShowSuccessModal(false)} style={{ padding: '8px 24px', fontSize: '15px', fontWeight: 'bold' }}>
                <i className="bi bi-check2"></i> OK
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoleAccessManagement;
