import React, { useState, useEffect } from 'react';

const DivisionManagement = () => {
  const [divisions, setDivisions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);

  // Fetch divisions from API
  const fetchDivisions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/divisions', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        credentials: 'include'
      });
      
      if (response.ok) {
        const data = await response.json();
        setDivisions(data.data || []);
      } else {
        console.error('Failed to fetch divisions');
        // Fallback to mock data if API fails
        setDivisions([
          { _id: '1', name: 'Engineering', code: 'ENG', description: 'Technical Department', employeeCount: 120 },
          { _id: '2', name: 'HR', code: 'HR', description: 'Human Resources', employeeCount: 25 },
          { _id: '3', name: 'Finance', code: 'FIN', description: 'Financial Department', employeeCount: 35 }
        ]);
      }
    } catch (error) {
      console.error('Error fetching divisions:', error);
      // Fallback to mock data
      setDivisions([
        { _id: '1', name: 'Engineering', code: 'ENG', description: 'Technical Department', employeeCount: 120 },
        { _id: '2', name: 'HR', code: 'HR', description: 'Human Resources', employeeCount: 25 },
        { _id: '3', name: 'Finance', code: 'FIN', description: 'Financial Department', employeeCount: 35 }
      ]);
    } finally {
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error for this field
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Validate form
  const validateForm = () => {
    const errors = {};
    if (!formData.name.trim()) errors.name = 'Division name is required';
    if (!formData.code.trim()) errors.code = 'Division code is required';
    return errors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const errors = validateForm();
    
    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    setSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/divisions', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        await fetchDivisions(); // Refresh the list
        setShowAddModal(false);
        setFormData({ name: '', code: '', description: '' });
        setFormErrors({});
      } else {
        const errorData = await response.json();
        alert(errorData.message || 'Failed to add division');
      }
    } catch (error) {
      console.error('Error adding division:', error);
      alert('Error adding division. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  // Use effect to fetch divisions on component mount
  useEffect(() => {
    fetchDivisions();
  }, []);

  if (loading) {
    return <div className="loading-container">Loading...</div>;
  }

  return (
    <div className="division-management">
      <div className="page-header">
        <h2><i className="bi bi-building"></i> Division Management</h2>
        <button className="btn btn-primary">
          <i className="bi bi-plus-circle"></i> Add Division
        </button>
      </div>

      <div className="divisions-grid">
        {divisions.map(division => (
          <div key={division.id} className="division-card">
            <h4>{division.name}</h4>
            <p>{division.description}</p>
            <div className="employee-count">
              <strong>{division.employeeCount}</strong> employees
            </div>
            <div className="actions">
              <button className="btn btn-sm btn-outline-primary">Edit</button>
              <button className="btn btn-sm btn-outline-danger">Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DivisionManagement;
